import React from 'react';
import { StoryboardContainer, StoryboardInstructions, StoryboardSection, SubmitButton } from './CommonComponents';

const Day3 = ({ storyboardSections, setStoryboardSections, submitDay, isReadyForNextDay }) => {
  return (
    <StoryboardContainer>
      <StoryboardInstructions>
        Create a three-panel storyboard that outlines your chosen solution. Each panel should represent a key step or moment in your solution:
        1. The problem or starting point
        2. The main action or change
        3. The result or outcome
        Describe each panel in detail, focusing on what the user sees and does.
      </StoryboardInstructions>
      {storyboardSections.map((section, index) => (
        <StoryboardSection
          key={index}
          value={section}
          onChange={(e) => {
            const newSections = [...storyboardSections];
            newSections[index] = e.target.value;
            setStoryboardSections(newSections);
          }}
          placeholder={`Describe panel ${index + 1}`}
        />
      ))}
      {isReadyForNextDay && (
        <SubmitButton onClick={submitDay}>
          Submit Storyboard and Move to Day 4
        </SubmitButton>
      )}
    </StoryboardContainer>
  );
};

export default Day3;
