import React, { useState } from 'react';
import styled from 'styled-components';
import Monday from './Monday';
import Tuesday from './Tuesday';
import Wednesday from './Wednesday';
import Thursday from './Thursday';
import Friday from './Friday';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #121212;
  color: #e0e0e0;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
`;

const DaySprint = ({ onUpdateOutput }) => {
  const [currentDay, setCurrentDay] = useState(1);
  const [sprintData, setSprintData] = useState({});

  const handleDayComplete = (day, data) => {
    setSprintData(prev => ({ ...prev, [day]: data }));
    onUpdateOutput(day, data);
    setCurrentDay(prev => prev + 1);
  };

  const renderCurrentDay = () => {
    switch(currentDay) {
      case 1:
        return <Monday onComplete={(data) => handleDayComplete(1, data)} />;
      case 2:
        return <Tuesday onComplete={(data) => handleDayComplete(2, data)} />;
      case 3:
        return <Wednesday onComplete={(data) => handleDayComplete(3, data)} />;
      case 4:
        return <Thursday onComplete={(data) => handleDayComplete(4, data)} />;
      case 5:
        return <Friday onComplete={(data) => handleDayComplete(5, data)} />;
      default:
        return <div>Sprint Complete!</div>;
    }
  };

  return (
    <SprintContainer>
      <MainContent>
        {renderCurrentDay()}
      </MainContent>
    </SprintContainer>
  );
};

export default DaySprint;
