import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance } from '../utils/sprintResources';
import OutputSidebar from './OutputSidebar';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #121212;
  color: #e0e0e0;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
`;

const Header = styled.h1`
  color: #3c8ce7;
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const Question = styled.h2`
  font-size: 1.8rem;
  margin-bottom: 20px;
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 15px;
  margin-bottom: 20px;
  border: none;
  border-radius: 25px;
  font-size: 1rem;
  background-color: #f8f9fa;
  color: #1e1e1e;
  resize: vertical;
  min-height: 100px;
`;

const Button = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  border-radius: 25px;
  padding: 15px 30px;
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #2a6cb0;
  }
`;

const FeedbackArea = styled.div`
  background-color: #f8f9fa;
  color: #1e1e1e;
  padding: 20px;
  border-radius: 15px;
  margin-top: 20px;
`;

const DaySprint = () => {
  const [currentDay, setCurrentDay] = useState(1);
  const [input, setInput] = useState('');
  const [feedback, setFeedback] = useState('');
  const [sprintData, setSprintData] = useState({});
  const [stickyNotes, setStickyNotes] = useState([]);

  const getCurrentPrompt = () => sprintGuidance[currentDay].prompt;

  const handleSubmit = async () => {
    const prompt = `Day ${currentDay} of Design Sprint. User input: "${input}". ${getCurrentPrompt()} Provide specific, constructive feedback as an expert design sprint coach. If the input is sufficient, suggest moving to the next task. If not, guide the user to improve their response.`;

    try {
      const response = await getGPTResponse(prompt);
      setFeedback(response);

      setSprintData(prev => ({
        ...prev,
        [currentDay]: [...(prev[currentDay] || []), { question: getCurrentPrompt(), answer: input }]
      }));

      if (response.toLowerCase().includes("next task") || response.toLowerCase().includes("move on")) {
        setTimeout(() => {
          setCurrentDay(prev => Math.min(prev + 1, 5));
          setInput('');
          setFeedback('');
        }, 3000);
      }
    } catch (error) {
      console.error("Error getting AI response:", error);
      setFeedback("I'm having trouble providing feedback right now. Please try again.");
    }
  };

  const addStickyNote = (note) => {
    setStickyNotes(prev => [...prev, note]);
  };

  useEffect(() => {
    const initialPrompt = `It's Day ${currentDay} of a Design Sprint. ${getCurrentPrompt()} Provide a brief, encouraging message to start the day as an expert design sprint coach.`;
    getGPTResponse(initialPrompt).then(setFeedback);
  }, [currentDay]);

  return (
    <SprintContainer>
      <MainContent>
        <Header>{`Day ${currentDay}: ${sprintGuidance[currentDay].goal}`}</Header>
        <Question>{getCurrentPrompt()}</Question>
        <TextArea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter your response here..."
        />
        <Button onClick={handleSubmit}>Submit</Button>
        {feedback && <FeedbackArea>{feedback}</FeedbackArea>}
      </MainContent>
      <OutputSidebar
        sprintData={sprintData}
        currentDay={currentDay}
        stickyNotes={stickyNotes}
        onAddStickyNote={addStickyNote}
      />
    </SprintContainer>
  );
};

export default DaySprint;
