import React, { useState, useEffect, useCallback, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance } from '../utils/sprintResources';
import OutputSidebar from './OutputSidebar';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #121212;
  color: #e0e0e0;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
`;

const Header = styled.h1`
  color: #3c8ce7;
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const ConversationBox = styled.div`
  height: 70vh;
  overflow-y: auto;
  border: 1px solid #3c8ce7;
  padding: 10px;
  margin-bottom: 20px;
  background-color: #2a2a2a;
  border-radius: 5px;
  display: flex;
  flex-direction: column;
`;

const Message = styled.div`
  background-color: ${props => props.isUser ? '#3c8ce7' : '#2a2a2a'};
  color: #e0e0e0;
  padding: 10px;
  border-radius: 15px;
  margin-bottom: 10px;
  max-width: 70%;
  align-self: ${props => props.isUser ? 'flex-end' : 'flex-start'};
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #3a3a3a;
  color: #e0e0e0;
  margin-top: 10px;
`;

const typing = keyframes`
  0% { opacity: 0.3; }
  50% { opacity: 1; }
  100% { opacity: 0.3; }
`;

