const DaySprint = () => {
    const [currentDay, setCurrentDay] = useState(1);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [sprintData, setSprintData] = useState({});
    const [isTyping, setIsTyping] = useState(false);
    const [isReadyForNextDay, setIsReadyForNextDay] = useState(false);
    const [timerSeconds, setTimerSeconds] = useState(480);
    const [isTimerRunning, setIsTimerRunning] = useState(false);
    const [crazyEightIdeas, setCrazyEightIdeas] = useState(Array(8).fill(''));
    const [storyboardSections, setStoryboardSections] = useState(['', '', '']);
    const [prototypeData, setPrototypeData] = useState({ coreElements: '', structureFlow: '', interactionsProcesses: '' });
    const [testingScenario, setTestingScenario] = useState('');
    const [userInteraction, setUserInteraction] = useState('');
    const [analysisNotes, setAnalysisNotes] = useState('');
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    const conversationEndRef = useRef(null);

    const scrollToBottom = () => {
        conversationEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [messages]);

    const getCurrentPrompt = useCallback(() => sprintGuidance[currentDay].prompt, [currentDay]);
    const getCurrentObjectives = useCallback(() => sprintGuidance[currentDay].objectives, [currentDay]);

    const moveToNextDay = useCallback(() => {
        setCurrentDay(prev => {
            const nextDay = Math.min(prev + 1, 5);
            setIsReadyForNextDay(false);
            if (prev === 2) {
                setCrazyEightIdeas(Array(8).fill(''));
            } else if (prev === 3) {
                setStoryboardSections(['', '', '']);
            }
            setIsTimerRunning(false);
            setTimerSeconds(480);
            return nextDay;
        });
    }, []);

    const checkDayCompletion = useCallback(() => {
        let isComplete = false;

        switch (currentDay) {
            case 1:
                isComplete = sprintData[1]?.["Long-term Goal"] && sprintData[1]?.["Critical Questions"];
                break;
            case 2:
                isComplete = crazyEightIdeas.every(idea => idea.trim() !== '');
                break;
            case 3:
                isComplete = storyboardSections.every(section => section.trim() !== '');
                break;
            case 4:
                isComplete = Object.values(prototypeData).every(value => value.trim() !== '');
                break;
            case 5:
                isComplete = testingScenario.trim() !== '' && userInteraction.trim() !== '' && analysisNotes.trim() !== '';
                break;
            default:
                isComplete = false;
        }

        setIsReadyForNextDay(isComplete);
    }, [currentDay, sprintData, crazyEightIdeas, storyboardSections, prototypeData, testingScenario, userInteraction, analysisNotes]);

    useEffect(() => {
        let interval;
        if (isTimerRunning && timerSeconds > 0) {
            interval = setInterval(() => {
                setTimerSeconds(seconds => seconds - 1);
            }, 1000);
        } else if (timerSeconds === 0) {
            setIsTimerRunning(false);
        }
        return () => clearInterval(interval);
    }, [isTimerRunning, timerSeconds]);

    const getInitialPrompt = useCallback(() => {
        if (currentDay === 1) {
            return `Welcome to our Mini Design Sprint! This compressed version of Jake Knapp and John Zeratsky's methodology will guide you through a rapid innovation process. We'll cover the essentials of a full sprint in just five short sessions.

  Let's kick off Day 1 by defining your long-term goal. This goal should be ambitious yet achievable, setting the direction for our entire sprint. What specific outcome are you aiming to achieve with this project? Think big and be as clear as possible.`;
        }
        return `Welcome to Day ${currentDay} of the Design Sprint. ${sprintGuidance[currentDay].prompt}`;
    }, [currentDay]);

    useEffect(() => {
        const loadInitialPrompt = async () => {
            setIsTyping(true);
            await new Promise(resolve => setTimeout(resolve, 1000));
            setIsTyping(false);
            const initialPrompt = getInitialPrompt();
            setMessages([{ isUser: false, text: initialPrompt }]);
        };

        loadInitialPrompt();
    }, [currentDay, getInitialPrompt]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!input.trim()) return;

        const userMessage = { isUser: true, text: input };
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setIsTyping(true);

        try {
            const context = messages.map(m => `${m.isUser ? 'User' : 'Relay'}: ${m.text}`).join('\n');
            const prompt = `Day ${currentDay} of Design Sprint. Context: ${context}\nUser: ${input}\n${getCurrentPrompt()}`;
            const response = await getGPTResponse(prompt);

            const aiMessage = { isUser: false, text: response };
            setMessages(prev => [...prev, aiMessage]);

            updateSprintData(input, response);
        } catch (error) {
            console.error('Error getting AI response:', error);
            setMessages(prev => [...prev, { isUser: false, text: "I'm having trouble right now. Please try again." }]);
        } finally {
            setIsTyping(false);
        }
    };

    const updateSprintData = (input, response) => {
        setSprintData(prev => {
            const updatedData = { ...prev };
            if (!updatedData[currentDay]) {
                updatedData[currentDay] = {};
            }
            updatedData[currentDay][`Input ${Object.keys(updatedData[currentDay]).length + 1}`] = input;
            updatedData[currentDay][`Response ${Object.keys(updatedData[currentDay]).length + 1}`] = response;
            return updatedData;
        });
        checkDayCompletion();
    };

    const submitCrazyEight = async () => {
        if (crazyEightIdeas.every(idea => idea.trim() !== '')) {
            setIsTyping(true);
            const ideasString = crazyEightIdeas.map((idea, index) => `Idea ${index + 1}: ${idea}`).join('\n');
            const prompt = `The user has completed the Crazy 8s exercise with the following ideas:\n${ideasString}\nPlease provide a brief analysis and feedback on these ideas, highlighting any particularly promising concepts or areas for further exploration.`;

            try {
                const response = await getGPTResponse(prompt);
                setMessages(prev => [...prev, { isUser: false, text: response }]);
                setSprintData(prev => ({
                    ...prev,
                    2: {
                        "Crazy 8s": crazyEightIdeas,
                        "Feedback": response
                    }
                }));
                setIsReadyForNextDay(true);
            } catch (error) {
                setMessages(prev => [...prev, { isUser: false, text: "I'm having trouble providing feedback on your Crazy 8 ideas. Please try submitting again." }]);
            } finally {
                setIsTyping(false);
            }
        } else {
            alert("Please complete all 8 ideas before submitting.");
        }
    };

    const updateCrazyEightIdea = (index, value) => {
        const newIdeas = [...crazyEightIdeas];
        newIdeas[index] = value;
        setCrazyEightIdeas(newIdeas);
    };

    const updateStoryboardSection = (index, value) => {
        const newSections = [...storyboardSections];
        newSections[index] = value;
        setStoryboardSections(newSections);
        checkDayCompletion();
    };

    const updatePrototypeData = (key, value) => {
        setPrototypeData(prev => ({ ...prev, [key]: value }));
        checkDayCompletion();
    };

    const startTimer = () => {
        setIsTimerRunning(true);
        setTimerSeconds(480);
    };

    const conductSimulatedInteraction = async () => {
        setIsTyping(true);
        const prompt = `You are a potential user interacting with the following product prototype: ${JSON.stringify(prototypeData)}. Please provide realistic responses and actions based on this testing scenario: ${testingScenario}`;
        try {
            const response = await getGPTResponse(prompt);
            setUserInteraction(response);
            setMessages(prev => [...prev, { isUser: false, text: response }]);
        } catch (error) {
            setMessages(prev => [...prev, { isUser: false, text: "Error: Unable to conduct simulated interaction." }]);
        } finally {
            setIsTyping(false);
        }
    };

    return (
        <SprintContainer>
            <MainContent>
                <Header>{`Day ${currentDay}: ${sprintGuidance[currentDay].goal}`}</Header>
                <ConversationBox>
                    {messages.map((message, index) => (
                        <Message key={index} isUser={message.isUser}>
                            {message.isUser ? 'You: ' : 'Relay: '}{message.text}
                        </Message>
                    ))}
                    {isTyping && <TypingIndicator><span></span><span></span><span></span></TypingIndicator>}
                    <div ref={conversationEndRef} />
                </ConversationBox>
                <form onSubmit={handleSubmit}>
                    <Input
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type your message here..."
                    />
                </form>
                {currentDay === 2 && (
                    <CrazyEightContainer>
                        <CrazyEightInstructions>
                            Crazy 8s is a fast sketching exercise where you'll create 8 distinct ideas in 8 minutes. 
                            The goal is to push beyond your first idea and generate a wide range of possibilities. 
                            Don't worry about quality or feasibility at this stage - focus on quantity and variety!
                        </CrazyEightInstructions>
                        <CopyButton onClick={startTimer}>Start Crazy 8s Timer</CopyButton>
                        <Timer>{Math.floor(timerSeconds / 60)}:{(timerSeconds % 60).toString().padStart(2, '0')}</Timer>
                        {crazyEightIdeas.map((idea, index) => (
                            <CrazyEightInput
                                key={index}
                                value={idea}
                                onChange={(e) => updateCrazyEightIdea(index, e.target.value)}
                                placeholder={`Crazy 8s Idea ${index + 1}`}
                            />
                        ))}
                        <SubmitButton onClick={submitCrazyEight}>
                            Submit Crazy 8s Ideas for Feedback
                        </SubmitButton>
                    </CrazyEightContainer>
                )}
                {currentDay === 3 && (
                    <StoryboardContainer>
                        <StoryboardInstructions>
                            Create a three-panel storyboard that outlines your chosen solution. Each panel should represent a key step or moment in your solution:
                            1. The problem or starting point
                            2. The main action or change
                            3. The result or outcome
                            Describe each panel in detail, focusing on what the user sees and does.
                        </StoryboardInstructions>
                        {storyboardSections.map((section, index) => (
                            <StoryboardSection
                                key={index}
                                value={section}
                                onChange={(e) => updateStoryboardSection(index, e.target.value)}
                                placeholder={`Panel ${index + 1}: ${index === 0 ? "Describe the problem or starting point" : index === 1 ? "Detail the main action or change" : "Illustrate the result or outcome"}`}
                            />
                        ))}
                    </StoryboardContainer>
                )}
                {currentDay === 4 && (
                    <PrototypeSection>
                        <h3>Design Your Conceptual Prototype</h3>
                        <StoryboardSection
                            value={prototypeData.coreElements}
                            onChange={(e) => updatePrototypeData('coreElements', e.target.value)}
                            placeholder="Describe core elements..."
                        />
                        <StoryboardSection
                            value={prototypeData.structureFlow}
                            onChange={(e) => updatePrototypeData('structureFlow', e.target.value)}
                            placeholder="Outline structure or flow..."
                        />
                        <StoryboardSection
                            value={prototypeData.interactionsProcesses}
                            onChange={(e) => updatePrototypeData('interactionsProcesses', e.target.value)}
                            placeholder="Detail interactions or processes..."
                        />
                    </PrototypeSection>
                )}
                {currentDay === 5 && (
                    <InteractionSection>
                        <h3>Simulated User/Stakeholder Testing</h3>
                        <StoryboardSection
                            value={testingScenario}
                            onChange={(e) => setTestingScenario(e.target.value)}
                            placeholder="Describe a testing scenario for your prototype..."
                        />
                        <CopyButton onClick={conductSimulatedInteraction}>Simulate User Interaction</CopyButton>
                        {userInteraction && (
                            <>
                                <h4>Simulated User Interaction:</h4>
                                <p>{userInteraction}</p>
                            </>
                        )}
                        <StoryboardSection
                            value={analysisNotes}
                            onChange={(e) => setAnalysisNotes(e.target.value)}
                            placeholder="Analyze the simulated feedback and consider potential iterations..."
                        />
                    </InteractionSection>
                )}
                {isReadyForNextDay && currentDay < 5 && (
                    <NextDayButton onClick={moveToNextDay}>
                        Move to Day {currentDay + 1}
                    </NextDayButton>
                )}
            </MainContent>
            {isSidebarOpen && (
                <OutputSidebar
                    sprintData={sprintData}
                    currentDay={currentDay}
                >
                    <ToggleSidebarButton onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
                        âœ•
                    </ToggleSidebarButton>
                </OutputSidebar>
            )}
        </SprintContainer>
    );
};

export default DaySprint;
