import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import AIChat from './AIChat';
import OutputSidebar from './OutputSidebar';
import { sprintGuidance } from '../utils/sprintResources';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #1a1a1a;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
`;

const DayTitle = styled.h2`
  font-size: 2rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const NextDayButton = styled.button`
  margin-top: 1rem;
`;

const DaySprint = ({ initialDay, onUpdateOutput, sprintOutput }) => {
  const [currentDay, setCurrentDay] = useState(initialDay);
  const [deliverables, setDeliverables] = useState(sprintOutput);

  const handleUpdateDeliverable = useCallback((day, deliverable) => {
    setDeliverables(prev => {
      const newDeliverables = { ...prev, [day]: deliverable };
      onUpdateOutput(day, deliverable);
      return newDeliverables;
    });
  }, [onUpdateOutput]);

  const handleNextDay = useCallback(() => {
    if (currentDay < 5) {
      setCurrentDay(prev => prev + 1);
    }
  }, [currentDay]);

  const { goal, tasks } = sprintGuidance[currentDay];

  return (
    <SprintContainer>
      <MainContent>
        <DayTitle>{`Day ${currentDay}: ${goal}`}</DayTitle>
        <AIChat 
          day={currentDay} 
          onUpdateDeliverable={handleUpdateDeliverable}
          onNextDay={handleNextDay}
        />
        {currentDay < 5 && (
          <NextDayButton onClick={handleNextDay}>
            Move to Day {currentDay + 1}
          </NextDayButton>
        )}
      </MainContent>
      <OutputSidebar 
        deliverables={deliverables} 
        currentDay={currentDay}
      />
    </SprintContainer>
  );
};

export default DaySprint;
