import React, { useState, useEffect, useCallback } from 'react';
import { getGPTResponse } from '../../utils/gptService';
import { sprintGuidance } from '../../utils/sprintResources';
import OutputSidebar from '../OutputSidebar';
import Day1 from './Day1';
import Day2 from './Day2';
import Day3 from './Day3';
import Day4 from './Day4';
import Day5 from './Day5';
import { SprintContainer, MainContent, Header, AIMessage, RelayIcon, TypingIndicator, StickyNotesSection, StickyNotesGrid, StickyNote, DeleteNote, StickyNoteInput, StickyNotePrompt, ConversationLog, LogEntry, CopyButton } from './CommonComponents';

const DaySprintEngine = () => {
  const [currentDay, setCurrentDay] = useState(1);
  const [input, setInput] = useState('');
  const [feedback, setFeedback] = useState('');
  const [sprintData, setSprintData] = useState({});
  const [stickyNotes, setStickyNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [conversationLog, setConversationLog] = useState([]);
  const [isReadyForNextDay, setIsReadyForNextDay] = useState(false);

  // Day-specific states
  const [crazyEightIdeas, setCrazyEightIdeas] = useState(Array(8).fill(''));
  const [storyboardSections, setStoryboardSections] = useState(['', '', '']);
  const [prototypeData, setPrototypeData] = useState({ coreElements: '', structureFlow: '', interactionsProcesses: '' });
  const [testingScenario, setTestingScenario] = useState('');
  const [userInteraction, setUserInteraction] = useState('');
  const [analysisNotes, setAnalysisNotes] = useState('');

  const getCurrentPrompt = useCallback(() => sprintGuidance[currentDay].prompt, [currentDay]);
  const getCurrentObjectives = useCallback(() => sprintGuidance[currentDay].objectives, [currentDay]);

  const handleSubmit = async () => {
    if (!input.trim()) return;
    setIsTyping(true);
    const userInput = input;
    setInput('');
    setConversationLog(prev => [...prev, { isUser: true, text: userInput }]);

    const prompt = `Day ${currentDay} of Design Sprint. User input: "${userInput}". ${getCurrentPrompt()} Provide guidance and suggestions to help the user develop their own ideas. Do not provide direct answers or solutions. Encourage the user to think critically and come up with their own responses.`;

    try {
      const response = await getGPTResponse(prompt);
      const cleanedResponse = response.replace('[SHOW_NEXT_DAY_BUTTON]', '').trim();
      setFeedback(cleanedResponse);
      setConversationLog(prev => [...prev, { isUser: false, text: cleanedResponse }]);

      // Update sprint data
      const currentObjectives = getCurrentObjectives();
      const currentObjective = currentObjectives[Object.keys(sprintData[currentDay] || {}).length];
      if (currentObjective) {
        let summary = await getGPTResponse(`Summarize the following user input for the Design Sprint Day ${currentDay}, objective: ${currentObjective}. User input: "${userInput}"`);
        setSprintData(prev => ({
          ...prev,
          [currentDay]: {
            ...prev[currentDay],
            [currentObjective]: summary
          }
        }));
      }
      checkDayCompletion();
    } catch (error) {
      console.error("Error getting AI response:", error);
      setFeedback("I'm having trouble providing feedback right now. Please try again.");
    } finally {
      setIsTyping(false);
    }
  };

  const moveToNextDay = useCallback(() => {
    setCurrentDay(prev => Math.min(prev + 1, 5));
    setFeedback('');
    setIsReadyForNextDay(false);
    // Reset day-specific states as needed
  }, []);

  const checkDayCompletion = useCallback(() => {
    // Implement day completion logic here
  }, [currentDay, sprintData, getCurrentObjectives]);

  const submitDay2 = useCallback(() => {
    if (crazyEightIdeas.every(idea => idea.trim() !== '')) {
      setSprintData(prev => ({
        ...prev,
        2: { "Crazy 8s": crazyEightIdeas.filter(idea => idea.trim() !== '') }
      }));
      moveToNextDay();
    } else {
      alert("Please complete all 8 ideas before submitting.");
    }
  }, [crazyEightIdeas, moveToNextDay]);

  // Implement submitDay3, submitDay4, submitDay5 similarly

  const addStickyNote = () => {
    if (newNote.trim()) {
      setStickyNotes(prev => [...prev, newNote]);
      setNewNote('');
    }
  };

  const deleteStickyNote = (index) => {
    setStickyNotes(prev => prev.filter((_, i) => i !== index));
  };

  const copyConversationLog = () => {
    const logText = conversationLog.map(entry => `**${entry.isUser ? 'User' : 'Relay'}**: ${entry.text}`).join('\n');
    navigator.clipboard.writeText(logText);
  };

  const conductSimulatedInteraction = async () => {
    // Implement simulated interaction logic here
  };

  const renderCurrentDay = () => {
    switch(currentDay) {
      case 1:
        return <Day1 handleSubmit={handleSubmit} input={input} setInput={setInput} />;
      case 2:
        return <Day2 
          crazyEightIdeas={crazyEightIdeas} 
          setCrazyEightIdeas={setCrazyEightIdeas} 
          submitDay={submitDay2}
          isReadyForNextDay={isReadyForNextDay}
        />;
      case 3:
        return <Day3 
          storyboardSections={storyboardSections} 
          setStoryboardSections={setStoryboardSections} 
          submitDay={submitDay3}
          isReadyForNextDay={isReadyForNextDay}
        />;
      case 4:
        return <Day4 
          prototypeData={prototypeData} 
          setPrototypeData={setPrototypeData} 
          submitDay={submitDay4}
          isReadyForNextDay={isReadyForNextDay}
        />;
      case 5:
        return <Day5 
          testingScenario={testingScenario}
          setTestingScenario={setTestingScenario}
          userInteraction={userInteraction}
          setUserInteraction={setUserInteraction}
          analysisNotes={analysisNotes}
          setAnalysisNotes={setAnalysisNotes}
          submitDay={submitDay5}
          conductSimulatedInteraction={conductSimulatedInteraction}
          isReadyForNextDay={isReadyForNextDay}
        />;
      default:
        return null;
    }
  };

  return (
    <SprintContainer>
      <MainContent>
        <Header>{`Day ${currentDay}: ${sprintGuidance[currentDay].goal}`}</Header>
        {isTyping ? (
          <TypingIndicator>
            <span></span>
            <span></span>
            <span></span>
          </TypingIndicator>
        ) : (
          feedback && (
            <AIMessage>
              <RelayIcon>Relay :)</RelayIcon> {feedback}
            </AIMessage>
          )
        )}
        {renderCurrentDay()}
        <StickyNotesSection>
          <StickyNotePrompt>Add your quick thoughts to the sticky note board:</StickyNotePrompt>
          <StickyNotesGrid>
            {stickyNotes.map((note, index) => (
              <StickyNote key={index}>
                {note}
                <DeleteNote onClick={() => deleteStickyNote(index)}>Ã—</DeleteNote>
              </StickyNote>
            ))}
          </StickyNotesGrid>
          <StickyNoteInput
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
            placeholder="Add a new note..."
            onKeyPress={(e) => e.key === 'Enter' && addStickyNote()}
          />
        </StickyNotesSection>
        <ConversationLog>
          <h3>Conversation Log (For Development)</h3>
          <CopyButton onClick={copyConversationLog}>Copy Log</CopyButton>
          {conversationLog.map((entry, index) => (
            <LogEntry key={index} isUser={entry.isUser}>
              <strong>{entry.isUser ? 'User' : 'Relay'}</strong>: {entry.text}
            </LogEntry>
          ))}
        </ConversationLog>
      </MainContent>
      <OutputSidebar
        sprintData={sprintData}
        currentDay={currentDay}
      />
    </SprintContainer>
  );
};

export default DaySprintEngine;
