export const states = {
    LONG_TERM_GOAL: 'LONG_TERM_GOAL',
    KEY_CHALLENGES: 'KEY_CHALLENGES',
    OPPORTUNITIES: 'OPPORTUNITIES',
    COMPLETED: 'COMPLETED'
  };

  export const createSprintDay = (day) => {
    switch(day) {
      case 1:
        return {
          initialState: states.LONG_TERM_GOAL,
          transitions: {
            [states.LONG_TERM_GOAL]: states.KEY_CHALLENGES,
            [states.KEY_CHALLENGES]: states.OPPORTUNITIES,
            [states.OPPORTUNITIES]: states.COMPLETED
          },
          prompts: {
            [states.LONG_TERM_GOAL]: "What is the long-term goal for your project?",
            [states.KEY_CHALLENGES]: "What's a key challenge you anticipate? (List at least 2)",
            [states.OPPORTUNITIES]: "What's an opportunity you see for achieving your goal?",
            [states.COMPLETED]: "Great job! You've completed Day 1. [SHOW_NEXT_DAY_BUTTON]"
          }
        };
      case 2:
        return {
          initialState: states.IDEA_GENERATION,
          transitions: {
            [states.IDEA_GENERATION]: states.IDEA_SELECTION,
            [states.IDEA_SELECTION]: states.COMPLETED
          },
          prompts: {
            [states.IDEA_GENERATION]: "Let's brainstorm solutions. What's an idea you have?",
            [states.IDEA_SELECTION]: "Which idea do you think is the most promising?",
            [states.COMPLETED]: "Excellent work on Day 2! [SHOW_NEXT_DAY_BUTTON]"
          }
        };
      // ... add cases for days 3, 4, and 5
    }
  };
