import React from 'react';
import styled from 'styled-components';
import { sprintGuidance } from '../utils/sprintResources';

const Sidebar = styled.div`
  width: 300px;
  background-color: #2a2a2a;
  padding: 1rem;
  overflow-y: auto;
`;

const Title = styled.h3`
  font-size: 1.2rem;
  margin-bottom: 1rem;
`;

const DaySection = styled.div`
  margin-bottom: 1rem;
`;

const DayTitle = styled.h4`
  font-size: 1rem;
  margin-bottom: 0.5rem;
  color: ${props => props.isCurrent ? '#3c8ce7' : 'inherit'};
`;

const Content = styled.div`
  font-size: 0.9rem;
  white-space: pre-wrap;
`;

const OutputSidebar = ({ deliverables, currentDay }) => {
  return (
    <Sidebar>
      <Title>Sprint Progress</Title>
      {Object.entries(sprintGuidance).map(([day, { goal }]) => (
        <DaySection key={day}>
          <DayTitle isCurrent={parseInt(day) === currentDay}>Day {day}: {goal}</DayTitle>
          <Content>
            {deliverables[day] ? deliverables[day] : "Not yet defined"}
          </Content>
        </DaySection>
      ))}
    </Sidebar>
  );
};

export default OutputSidebar;
