export const sprintGuidance = {
  1: {
    goal: "Set Long-Term Goal & Identify Critical Questions",
    objectives: [
      "Set the Long-Term Goal",
      "Identify Critical Questions"
    ],
    prompt: "Let's start by defining your long-term goal for this project. What specific, measurable outcome are you aiming to achieve? Then, identify 2-3 critical questions that address potential challenges we need to overcome to reach this goal."
  },
  2: {
    goal: "Crazy 8s Ideation",
    objectives: [
      "Crazy 8s: Rapidly sketch eight different ideas in eight minutes"
    ],
    prompt: "It's time for the Crazy 8s exercise. Quickly sketch out 8 diverse ideas related to your challenge in 8 minutes. Focus on quantity and variety rather than quality or feasibility."
  },
  3: {
    goal: "Solution Sketch",
    objectives: [
      "Create a detailed, three-panel storyboard for the most promising idea"
    ],
    prompt: "Based on your Crazy 8s ideas, choose the most promising one and create a detailed three-panel storyboard. Describe your process and final solution sketch."
  },
  4: {
    goal: "Design Conceptual Prototype",
    objectives: [
      "Describe core elements",
      "Outline structure or flow",
      "Detail interactions or processes"
    ],
    prompt: "Today, we'll design a conceptual prototype of your solution. Describe the core elements, outline the overall structure or flow, and detail how users or participants would interact with or experience your solution. Focus on creating a vivid, detailed description that brings your idea to life, regardless of whether it's a product, service, process, or any other type of solution."
  },
  5: {
    goal: "Simulated User/Stakeholder Testing",
    objectives: [
      "Prepare testing scenario",
      "Conduct simulated interaction",
      "Analyze feedback and iterate"
    ],
    prompt: "On this final day, we'll simulate testing with users or stakeholders. Prepare a scenario for testing your conceptual prototype, then engage in a role-play interaction with AI Relay acting as a user or stakeholder. Afterward, analyze the feedback and consider potential iterations."
  }
};

export const getSprintTip = (day) => {
  const tips = [
    "Keep your long-term goal in mind as you identify critical questions.",
    "During Crazy 8s, focus on quantity and variety of ideas rather than perfection.",
    "When creating your solution sketch, focus on telling a clear story across the three panels.",
    "When prototyping, focus on creating a realistic façade rather than building full functionality.",
    "During user interviews, focus on observing behavior and listening more than talking."
  ];
  return tips[day - 1] || "Remember to apply the core principles throughout your sprint!";
};
