import React, { useState } from 'react';
import styled from 'styled-components';
import { sprintGuidance } from '../utils/sprintResources';

const Sidebar = styled.div`
  width: 300px;
  background-color: #1a1a1a;
  padding: 1.5rem;
  overflow-y: auto;
  color: #e0e0e0;
  font-family: 'Futura', 'Avenir', 'Proxima Nova', 'Helvetica', sans-serif;
  border-left: 1px solid #2a2a2a;
  height: 100vh;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  color: #ffffff;
`;

const DaySection = styled.div`
  margin-bottom: 1.5rem;
`;

const DayTitle = styled.h3`
  font-size: 1.1rem;
  margin-bottom: 0.5rem;
  color: ${props => props.isCurrent ? '#3c8ce7' : '#ffffff'};
`;

const Content = styled.div`
  font-size: 0.9rem;
  white-space: pre-wrap;
  color: #b0b0b0;
`;

const StickyNotesSection = styled.div`
  margin-top: 2rem;
`;

const StickyNote = styled.div`
  background-color: #feff9c;
  padding: 0.75rem;
  margin-bottom: 0.75rem;
  border-radius: 3px;
  color: #000000;
`;

const StickyNoteInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  margin-bottom: 0.75rem;
  border: none;
  border-radius: 3px;
  background-color: #2a2a2a;
  color: #e0e0e0;
`;

const OutputSidebar = ({ sprintData, currentDay, stickyNotes, onAddStickyNote }) => {
  const [newNote, setNewNote] = useState('');

  const handleAddNote = () => {
    if (newNote.trim()) {
      onAddStickyNote(newNote);
      setNewNote('');
    }
  };

  return (
    <Sidebar>
      <Title>Sprint Progress</Title>
      {Object.entries(sprintGuidance).map(([day, { goal }]) => (
        <DaySection key={day}>
          <DayTitle isCurrent={parseInt(day) === currentDay}>Day {day}: {goal}</DayTitle>
          <Content>
            {sprintData[day] ? (
              sprintData[day].map((item, index) => (
                <div key={index}>
                  <strong>Q: {item.question}</strong>
                  <p>A: {item.answer}</p>
                </div>
              ))
            ) : (
              "Not yet defined"
            )}
          </Content>
        </DaySection>
      ))}
      <StickyNotesSection>
        <Title>Sticky Notes</Title>
        {stickyNotes.map((note, index) => (
          <StickyNote key={index}>{note}</StickyNote>
        ))}
        <StickyNoteInput
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
          placeholder="Add a new note..."
          onKeyPress={(e) => e.key === 'Enter' && handleAddNote()}
        />
      </StickyNotesSection>
    </Sidebar>
  );
};

export default OutputSidebar;
