export const getGPTResponse = async (prompt) => {
    try {
      console.log('Environment variables:', process.env);
      const apiKey = process.env.REACT_APP_GPT_API_KEY?.trim();
      console.log('API Key:', apiKey);
      if (!apiKey) {
        throw new Error('OpenAI API key is missing');
      }

      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo",
          messages: [{ role: "user", content: prompt }],
          temperature: 0.7
        })
      });

      if (!response.ok) {
        const errorBody = await response.text();
        throw new Error(`HTTP error! status: ${response.status}, body: ${errorBody}`);
      }

      const data = await response.json();
      if (!data.choices || data.choices.length === 0) {
        throw new Error('No choices returned from API');
      }
      return data.choices[0].message.content;
    } catch (error) {
      console.error('Error in getGPTResponse:', error);
      let errorMessage = "There was an error processing your request. ";
      if (error.message.includes('OpenAI API key is missing')) {
        errorMessage += "The API key is missing. Please check your configuration.";
      } else if (error.message.includes('HTTP error')) {
        errorMessage += "There was an issue connecting to the AI service. Please try again later.";
      } else if (error.message.includes('No choices returned from API')) {
        errorMessage += "The AI service did not return a valid response. Please try again.";
      } else {
        errorMessage += "Please try again.";
      }
      throw new Error(errorMessage);
    }
  };
