import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance, getSprintTip } from '../utils/sprintResources';
import OutputSidebar from './OutputSidebar';
import InputWithSendIcon from './InputWithSendIcon';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #121212;
  color: #e0e0e0;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
`;

const Header = styled.h1`
  color: #3c8ce7;
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const Objectives = styled.ul`
  margin-bottom: 20px;
  color: #b0b0b0;
`;

const Question = styled.h2`
  font-size: 1.8rem;
  margin-bottom: 20px;
`;

const FeedbackArea = styled.div`
  background-color: #f8f9fa;
  color: #1e1e1e;
  padding: 20px;
  border-radius: 15px;
  margin-top: 20px;
  margin-bottom: 20px;
`;

const NextDayButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 20px;
  align-self: flex-start;
`;

const StickyNotesSection = styled.div`
  margin-top: 2rem;
`;

const StickyNote = styled.div`
  background-color: #feff9c;
  padding: 0.75rem;
  margin-bottom: 0.75rem;
  border-radius: 3px;
  color: #000000;
`;

const StickyNoteInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  margin-bottom: 0.75rem;
  border: none;
  border-radius: 3px;
  background-color: #2a2a2a;
  color: #e0e0e0;
`;

const StickyNotePrompt = styled.p`
  color: #b0b0b0;
  font-style: italic;
  margin-bottom: 10px;
`;

const DaySprint = () => {
  const [currentDay, setCurrentDay] = useState(1);
  const [input, setInput] = useState('');
  const [feedback, setFeedback] = useState('');
  const [sprintData, setSprintData] = useState({});
  const [stickyNotes, setStickyNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [isReadyForNextDay, setIsReadyForNextDay] = useState(false);

  const getCurrentPrompt = useCallback(() => sprintGuidance[currentDay].prompt, [currentDay]);
  const getCurrentObjectives = useCallback(() => sprintGuidance[currentDay].objectives, [currentDay]);

  const handleSubmit = async () => {
    const prompt = `Day ${currentDay} of Design Sprint. User input: "${input}". ${getCurrentPrompt()} Provide specific, constructive feedback as an expert design sprint coach. If the input is sufficient to meet the day's objectives, suggest moving to the next day. If not, guide the user to improve their response.`;

    try {
      const response = await getGPTResponse(prompt);
      setFeedback(response);

      setSprintData(prev => ({
        ...prev,
        [currentDay]: [...(prev[currentDay] || []), { question: getCurrentPrompt(), answer: input }]
      }));
      setInput('');

      if (response.toLowerCase().includes("next day") || response.toLowerCase().includes("move on")) {
        const summaryPrompt = `Summarize and clarify the following input for Day ${currentDay} of the Design Sprint: ${input}`;
        const summary = await getGPTResponse(summaryPrompt);
        setSprintData(prev => ({
          ...prev,
          [currentDay]: [...prev[currentDay], { question: "Summary", answer: summary }]
        }));
        setIsReadyForNextDay(true);
      }
    } catch (error) {
      console.error("Error getting AI response:", error);
      setFeedback("I'm having trouble providing feedback right now. Please try again.");
    }
  };

  const moveToNextDay = () => {
    setCurrentDay(prev => Math.min(prev + 1, 5));
    setFeedback('');
    setIsReadyForNextDay(false);
  };

  const addStickyNote = () => {
    if (newNote.trim()) {
      setStickyNotes(prev => [...prev, newNote]);
      setNewNote('');
    }
  };

  useEffect(() => {
    const initialPrompt = `Welcome to Day ${currentDay} of the Design Sprint. ${getCurrentPrompt()} As an expert design sprint coach, provide a brief, encouraging message to start the day.`;
    getGPTResponse(initialPrompt).then(setFeedback);
  }, [currentDay, getCurrentPrompt]);

  return (
    <SprintContainer>
      <MainContent>
        <Header>{`Day ${currentDay}: ${sprintGuidance[currentDay].goal}`}</Header>
        <Objectives>
          {getCurrentObjectives().map((objective, index) => (
            <li key={index}>{objective}</li>
          ))}
        </Objectives>
        <Question>{getCurrentPrompt()}</Question>
        <InputWithSendIcon
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onSubmit={handleSubmit}
        />
        {feedback && <FeedbackArea>{feedback}</FeedbackArea>}
        {isReadyForNextDay && <NextDayButton onClick={moveToNextDay}>Move to Day {currentDay + 1}</NextDayButton>}

        <StickyNotesSection>
          <StickyNotePrompt>Add your quick thoughts to the sticky note board:</StickyNotePrompt>
          {stickyNotes.map((note, index) => (
            <StickyNote key={index}>{note}</StickyNote>
          ))}
          <StickyNoteInput
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
            placeholder="Add a new note..."
            onKeyPress={(e) => e.key === 'Enter' && addStickyNote()}
          />
        </StickyNotesSection>
      </MainContent>
      <OutputSidebar
        sprintData={sprintData}
        currentDay={currentDay}
      />
    </SprintContainer>
  );
};

export default DaySprint;
