export const sprintGuidance = {
  1: {
    goal: "Define the long-term goal and identify challenges",
    tasks: [
      "Clearly state the long-term goal",
      "Identify key challenges and opportunities",
      "Establish direction for the sprint"
    ],
    prompt: "Let's start by defining your long-term goal for this project. What specific outcome are you aiming to achieve?"
  },
  2: {
    goal: "Generate potential solutions",
    tasks: [
      "Explore existing ideas",
      "Sketch new solutions",
      "Address identified challenges"
    ],
    prompt: "Based on yesterday's goal and challenges, let's brainstorm solutions. What ideas do you have to address the main challenge?"
  },
  3: {
    goal: "Decide on the best solution",
    tasks: [
      "Evaluate solutions from Day 2",
      "Select the most promising idea",
      "Create a plan for prototyping"
    ],
    prompt: "Looking at the solutions we generated yesterday, which one stands out as the most promising to prototype?"
  },
  4: {
    goal: "Build a tangible prototype",
    tasks: [
      "Create a realistic facade of your solution",
      "Focus on key features for testing",
      "Prepare for user feedback"
    ],
    prompt: "It's time to build a prototype of your chosen solution. What are the core features you want to include in this prototype?"
  },
  5: {
    goal: "Test and gather insights",
    tasks: [
      "Conduct user testing",
      "Gather and analyze feedback",
      "Identify areas for improvement"
    ],
    prompt: "Today we'll test your prototype. What specific aspects of your prototype do you want to focus on during user testing?"
  }
};

// ... (getSprintTip function remains unchanged)

  export const getSprintTip = (day) => {
    const tips = [
      "Remember to stay focused on your long-term goal throughout the sprint.",
      "Don't be afraid to think outside the box when generating solutions.",
      "Trust your intuition when selecting the best idea to prototype.",
      "Keep your prototype simple but realistic enough for meaningful feedback.",
      "Look for patterns in user feedback to guide your next steps."
    ];
    return tips[day - 1] || "Keep pushing forward!";
  };
