import React from 'react';
import { InteractionSection, StoryboardSection, CopyButton, SubmitButton } from './CommonComponents';

const Day5 = ({ testingScenario, setTestingScenario, userInteraction, setUserInteraction, analysisNotes, setAnalysisNotes, submitDay, conductSimulatedInteraction, isReadyForNextDay }) => {
  return (
    <InteractionSection>
      <h3>Simulated User/Stakeholder Testing</h3>
      <StoryboardSection
        value={testingScenario}
        onChange={(e) => setTestingScenario(e.target.value)}
        placeholder="Prepare testing scenario..."
      />
      <CopyButton onClick={conductSimulatedInteraction}>Conduct Simulated Interaction</CopyButton>
      {userInteraction && (
        <>
          <h4>User/Stakeholder Interaction:</h4>
          <p>{userInteraction}</p>
        </>
      )}
      <StoryboardSection
        value={analysisNotes}
        onChange={(e) => setAnalysisNotes(e.target.value)}
        placeholder="Analyze feedback and consider iterations..."
      />
      {isReadyForNextDay && (
        <SubmitButton onClick={submitDay}>
          Submit Testing Results and Complete Sprint
        </SubmitButton>
      )}
    </InteractionSection>
  );
};

export default Day5;
