export const sprintGuidance = {
  1: {
    goal: "Define the long-term goal and identify challenges",
    tasks: [
      "State the long-term goal",
      "List key challenges",
      "Identify opportunities"
    ],
    prompt: "Let's start by defining your long-term goal for this project. What specific outcome are you aiming to achieve?"
  },
  2: {
    goal: "Generate potential solutions",
    tasks: [
      "Brainstorm ideas",
      "Sketch solutions",
      "Consider existing solutions to adapt"
    ],
    prompt: "Based on yesterday's goal and challenges, let's brainstorm solutions. What ideas do you have to address the main challenge?"
  },
  3: {
    goal: "Decide on the best solution",
    tasks: [
      "Review generated solutions",
      "Evaluate feasibility and impact",
      "Select the most promising idea"
    ],
    prompt: "Looking at the solutions we generated yesterday, which one stands out as the most promising to prototype?"
  },
  4: {
    goal: "Plan the prototype",
    tasks: [
      "Define core features",
      "Outline user flow",
      "Decide on tools and resources"
    ],
    prompt: "Let's plan your prototype. What are the essential features and user flow you want to include?"
  },
  5: {
    goal: "Prepare for testing",
    tasks: [
      "Define testing criteria",
      "Create a test script",
      "Identify potential testers"
    ],
    prompt: "To prepare for testing your prototype, what specific aspects do you want to focus on during user testing?"
  }
};

export const getSprintTip = (day) => {
  const tips = [
    "Remember to keep your long-term goal in mind throughout the sprint.",
    "Don't be afraid to think outside the box when generating solutions.",
    "Consider both feasibility and potential impact when selecting your solution.",
    "Focus on the core features that will best demonstrate your concept.",
    "Think about what specific feedback would be most valuable for improving your prototype."
  ];
  return tips[day - 1] || "Keep pushing forward!";
};
