export const sprintGuidance = {
    1: {
      goal: "Set the foundation for your sprint",
      tasks: [
        "Clearly understand the long-term goal of your project",
        "Identify key challenges and opportunities",
        "Establish direction and focus for the sprint"
      ],
      prompt: "Let's start by defining your long-term goal. What are you aiming to achieve with this project?"
    },
    2: {
      goal: "Generate potential solutions",
      tasks: [
        "Explore and remix existing ideas",
        "Sketch out new, detailed solutions",
        "Address challenges identified on Day 1"
      ],
      prompt: "Great! Now let's brainstorm solutions. What ideas come to mind to address your main challenge?"
    },
    3: {
      goal: "Make key decisions on prototype",
      tasks: [
        "Evaluate solutions from Day 2",
        "Select the most promising ideas",
        "Create a plan for prototyping"
      ],
      prompt: "It's time to decide on the best solution. Which idea stands out as the most promising?"
    },
    4: {
      goal: "Build a tangible prototype",
      tasks: [
        "Create a realistic facade of your solution",
        "Focus on key features for testing",
        "Prepare for user feedback"
      ],
      prompt: "Let's outline your prototype. What are the core features you want to include?"
    },
    5: {
      goal: "Test and gather insights",
      tasks: [
        "Conduct user testing",
        "Gather and analyze feedback",
        "Identify areas for improvement"
      ],
      prompt: "Time to test your prototype. What specific aspects do you want to focus on during user testing?"
    }
  };

  export const getSprintTip = (day) => {
    const tips = [
      "Remember to stay focused on your long-term goal throughout the sprint.",
      "Don't be afraid to think outside the box when generating solutions.",
      "Trust your intuition when selecting the best idea to prototype.",
      "Keep your prototype simple but realistic enough for meaningful feedback.",
      "Look for patterns in user feedback to guide your next steps."
    ];
    return tips[day - 1] || "Keep pushing forward!";
  };
