import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import AIChat from './AIChat';
import OutputSidebar from './OutputSidebar';
import { sprintGuidance } from '../utils/sprintResources';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: #121212;
  color: #e0e0e0;
  font-family: 'Roboto', sans-serif;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
`;

const DayTitle = styled.h2`
  font-size: 2rem;
  margin-bottom: 1rem;
  color: #ffffff;
`;

const DaySummary = styled.div`
  background-color: #2a2a2a;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const ToggleSidebarButton = styled.button`
  position: fixed;
  top: 10px;
  right: ${props => props.isSidebarOpen ? '310px' : '10px'};
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 5px 10px;
  font-size: 0.8rem;
  cursor: pointer;
  border-radius: 5px;
  z-index: 1000;
  transition: right 0.3s ease;
`;

const DaySprint = ({ initialDay, onUpdateOutput, sprintOutput }) => {
  const [currentDay, setCurrentDay] = useState(initialDay);
  const [deliverables, setDeliverables] = useState(sprintOutput);
  const [stickyNotes, setStickyNotes] = useState([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const handleUpdateDeliverable = useCallback((day, deliverable) => {
    setDeliverables(prev => {
      const newDeliverables = { ...prev, [day]: deliverable };
      onUpdateOutput(day, deliverable);
      return newDeliverables;
    });
  }, [onUpdateOutput]);

  const handleNextDay = useCallback(() => {
    if (currentDay < 5) {
      setCurrentDay(prev => prev + 1);
      // Trigger Relay's prompt for the new day
      // You'll need to implement this in AIChat component
    }
  }, [currentDay]);

  const handleAddStickyNote = useCallback((note) => {
    setStickyNotes(prev => [...prev, note]);
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(prev => !prev);
  };

  const { goal, tasks } = sprintGuidance[currentDay];

  return (
    <SprintContainer>
      <MainContent>
        <DayTitle>{`Day ${currentDay}: ${goal}`}</DayTitle>
        <DaySummary>
          <h3>Today's Tasks:</h3>
          <ul>
            {tasks.map((task, index) => (
              <li key={index}>{task}</li>
            ))}
          </ul>
        </DaySummary>
        <AIChat 
          day={currentDay} 
          onUpdateDeliverable={handleUpdateDeliverable}
          onNextDay={handleNextDay}
        />
      </MainContent>
      {isSidebarOpen && (
        <OutputSidebar 
          deliverables={deliverables} 
          currentDay={currentDay}
          stickyNotes={stickyNotes}
          onAddStickyNote={handleAddStickyNote}
        />
      )}
      <ToggleSidebarButton onClick={toggleSidebar} isSidebarOpen={isSidebarOpen}>
        {isSidebarOpen ? 'Close Sidebar' : 'Open Sidebar'}
      </ToggleSidebarButton>
    </SprintContainer>
  );
};

export default DaySprint;
