import React from 'react';
import styled from 'styled-components';
import { sprintGuidance } from '../utils/sprintResources';

const Sidebar = styled.div`
  width: 300px;
  background-color: #1a1a1a;
  padding: 1.5rem;
  overflow-y: auto;
  color: #e0e0e0;
  font-family: 'Futura', 'Avenir', 'Proxima Nova', 'Helvetica', sans-serif;
  border-left: 1px solid #2a2a2a;
  height: 100vh;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  color: #ffffff;
`;

const DaySection = styled.div`
  margin-bottom: 1.5rem;
`;

const DayTitle = styled.h3`
  font-size: 1.1rem;
  margin-bottom: 0.5rem;
  color: ${props => props.isCurrent ? '#3c8ce7' : '#ffffff'};
`;

const ObjectiveTitle = styled.h4`
  font-size: 1rem;
  margin-top: 0.5rem;
  margin-bottom: 0.25rem;
  color: #b0b0b0;
`;

const ObjectiveContent = styled.div`
  font-size: 0.9rem;
  white-space: pre-wrap;
  color: #e0e0e0;
  margin-left: 0.5rem;
`;

const AIFeedbackTitle = styled.h4`
  font-size: 0.95rem;
  margin-top: 1rem;
  margin-bottom: 0.25rem;
  color: #3c8ce7;
`;

const AIFeedbackContent = styled.p`
  font-size: 0.85rem;
  color: #b0b0b0;
  font-style: italic;
`;

const OutputSidebar = ({ sprintData, currentDay }) => {
    const renderObjectiveContent = (day, objective, content) => {
        if (Array.isArray(content)) {
          return (
            <ul>
              {content.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          );
        } else if (typeof content === 'string') {
          return <p>{content}</p>;
        } else {
          return <p>Not yet defined</p>;
        }
      };

    return (
      <Sidebar>
        <Title>Sprint Progress</Title>
        {Object.entries(sprintGuidance).map(([day, { goal, objectives }]) => (
          <DaySection key={day}>
            <DayTitle isCurrent={parseInt(day) === currentDay}>Day {day}: {goal}</DayTitle>
            {objectives.map((objective, index) => (
              <div key={index}>
                <ObjectiveTitle>{objective}</ObjectiveTitle>
                <ObjectiveContent>
                  {sprintData[day] && sprintData[day][objective] 
                    ? renderObjectiveContent(day, objective, sprintData[day][objective])
                    : "Not yet defined"}
                </ObjectiveContent>
              </div>
            ))}
            {sprintData[day] && sprintData[day]["Feedback"] && (
              <>
                <AIFeedbackTitle>AI Feedback:</AIFeedbackTitle>
                <AIFeedbackContent>{sprintData[day]["Feedback"]}</AIFeedbackContent>
              </>
            )}
          </DaySection>
        ))}
      </Sidebar>
    );
  };

  export default OutputSidebar;
