import React, { useState } from 'react';
import styled from 'styled-components';
import { sprintGuidance } from '../utils/sprintResources';

const Sidebar = styled.div`
  width: 300px;
  background-color: #2a2a2a;
  padding: 1rem;
  overflow-y: auto;
`;

const Title = styled.h3`
  font-size: 1.2rem;
  margin-bottom: 1rem;
`;

const DaySection = styled.div`
  margin-bottom: 1rem;
`;

const DayTitle = styled.h4`
  font-size: 1rem;
  margin-bottom: 0.5rem;
  color: ${props => props.isCurrent ? '#3c8ce7' : 'inherit'};
`;

const Content = styled.div`
  font-size: 0.9rem;
  white-space: pre-wrap;
`;

const StickyNotesSection = styled.div`
  margin-top: 2rem;
`;

const StickyNote = styled.div`
  background-color: #feff9c;
  padding: 0.5rem;
  margin-bottom: 0.5rem;
  border-radius: 3px;
  color: #000000;
`;

const StickyNoteInput = styled.input`
  width: 100%;
  padding: 0.5rem;
  margin-bottom: 0.5rem;
  border: none;
  border-radius: 3px;
`;

const OutputSidebar = ({ deliverables, currentDay, stickyNotes, onAddStickyNote }) => {
  const [newNote, setNewNote] = useState('');

  const handleAddNote = () => {
    if (newNote.trim()) {
      onAddStickyNote(newNote);
      setNewNote('');
    }
  };

  return (
    <Sidebar>
      <Title>Sprint Progress</Title>
      {Object.entries(sprintGuidance).map(([day, { goal }]) => (
        <DaySection key={day}>
          <DayTitle isCurrent={parseInt(day) === currentDay}>Day {day}: {goal}</DayTitle>
          <Content>
            {deliverables[day] ? deliverables[day] : "Not yet defined"}
          </Content>
        </DaySection>
      ))}
      <StickyNotesSection>
        <Title>Sticky Notes</Title>
        {stickyNotes.map((note, index) => (
          <StickyNote key={index}>{note}</StickyNote>
        ))}
        <StickyNoteInput
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
          placeholder="Add a new note..."
          onKeyPress={(e) => e.key === 'Enter' && handleAddNote()}
        />
      </StickyNotesSection>
    </Sidebar>
  );
};

export default OutputSidebar;
