  margin-top: 1rem;
  padding: 1rem;
  background-color: #2a2a2a;
  border-radius: 5px;
`;

const CrazyEightInstructions = styled.p`
  margin-bottom: 1rem;
  color: #e0e0e0;
`;

const CrazyEightInput = styled.input`
  width: 100%;
  padding: 0.5rem;
  margin-bottom: 0.5rem;
  border: 1px solid #3c8ce7;
  border-radius: 5px;
`;

const AddIdeaButton = styled.button`
  background-color: #3c8ce7;
  color: white;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 5px;
  cursor: pointer;
  margin-bottom: 1rem;
`;

const StoryboardContainer = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background-color: #2a2a2a;
  border-radius: 5px;
`;

const StoryboardInstructions = styled.p`
  margin-bottom: 1rem;
  color: #e0e0e0;
`;

const StoryboardSection = styled.textarea`
  width: 100%;
  height: 150px;
  margin-top: 1rem;
  padding: 0.5rem;
  border-radius: 5px;
`;

const EndTimerButton = styled(CopyButton)`
  margin-top: 1rem;
`;

const SubmitButton = styled(CopyButton)`
  margin-top: 1rem;
  font-size: 1.2rem;
  padding: 1rem 2rem;
`;

const DaySprint = () => {
  const [currentDay, setCurrentDay] = useState(1);
  const [input, setInput] = useState('');
  const [feedback, setFeedback] = useState('');
  const [sprintData, setSprintData] = useState({});
  const [stickyNotes, setStickyNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [conversationLog, setConversationLog] = useState([]);
  const [timerSeconds, setTimerSeconds] = useState(480); // 8 minutes
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [crazyEightIdeas, setCrazyEightIdeas] = useState(['']);
  const [storyboardSections, setStoryboardSections] = useState(['', '', '']);
  const [isCrazyEightComplete, setIsCrazyEightComplete] = useState(false);
  const [isReadyForNextDay, setIsReadyForNextDay] = useState(false);

  const getCurrentPrompt = useCallback(() => sprintGuidance[currentDay].prompt, [currentDay]);
  const getCurrentObjectives = useCallback(() => sprintGuidance[currentDay].objectives, [currentDay]);

  const handleSubmit = async () => {
    if (!input.trim()) return;

    setIsTyping(true);
    const userInput = input;
    setInput('');

    setConversationLog(prev => [...prev, { isUser: true, text: userInput }]);

    const prompt = `Day ${currentDay} of Design Sprint. User input: "${userInput}". ${getCurrentPrompt()} Provide guidance and suggestions to help the user develop their own ideas. Do not provide direct answers or solutions. Encourage the user to think critically and come up with their own responses.`;

    try {
      const response = await getGPTResponse(prompt);
      const cleanedResponse = response.replace('[SHOW_NEXT_DAY_BUTTON]', '').trim();
      setFeedback(cleanedResponse);
      setConversationLog(prev => [...prev, { isUser: false, text: cleanedResponse }]);

      const currentObjectives = getCurrentObjectives();
      const currentObjective = currentObjectives[Object.keys(sprintData[currentDay] || {}).length];

      if (currentObjective) {
        let summary;
        if (currentDay === 2) {
          if (currentObjective === "Crazy 8s") {
            summary = crazyEightIdeas.filter(idea => idea.trim() !== '');
          } else if (currentObjective === "Solution Sketch") {
            summary = storyboardSections.map((section, index) => `Panel ${index + 1}: ${section}`);
          }
        } else {
          const summaryPrompt = `Summarize and refine the following user input for the Design Sprint Day ${currentDay}, objective: ${currentObjective}. User input: "${userInput}". Provide a concise, clear summary that captures the essence of the user's input.`;
          summary = await getGPTResponse(summaryPrompt);
        }

        setSprintData(prev => ({
          ...prev,
          [currentDay]: {
            ...prev[currentDay],
            [currentObjective]: summary
          }
        }));
      }

      checkDayCompletion();
    } catch (error) {
      console.error("Error getting AI response:", error);
      setFeedback("I'm having trouble providing feedback right now. Please try again.");
      setConversationLog(prev => [...prev, { isUser: false, text: "Error: Unable to process response." }]);
    } finally {
      setIsTyping(false);
    }
  };

  const submitDay2 = () => {
    if (crazyEightIdeas.filter(idea => idea.trim() !== '').length === 8 &&
        storyboardSections.every(section => section.trim() !== '')) {
      setSprintData(prev => ({
        ...prev,
        [currentDay]: {
          ...prev[currentDay],
          "Crazy 8s": crazyEightIdeas,
          "Solution Sketch": storyboardSections.map((section, index) => `Panel ${index + 1}: ${section}`)
        }
      }));
      moveToNextDay();
    } else {
      alert("Please complete both Crazy 8s and the Storyboard before submitting.");
    }
  };

  const moveToNextDay = useCallback(() => {
    setCurrentDay(prev => {
      const nextDay = Math.min(prev + 1, 5);
      // Reset states for the next day
      setFeedback('');
      setIsReadyForNextDay(false);
      setCrazyEightIdeas(['']);
      setStoryboardSections(['', '', '']);
      setIsCrazyEightComplete(false);
      setIsTimerRunning(false);
      setTimerSeconds(480);
      return nextDay;
    });
  }, []);

  const checkDayCompletion = useCallback(() => {
    if (currentDay === 2) {
      const isCrazyEightsComplete = crazyEightIdeas.filter(idea => idea.trim() !== '').length === 8;
      const isStoryboardComplete = storyboardSections.every(section => section.trim() !== '');
      setIsReadyForNextDay(isCrazyEightsComplete && isStoryboardComplete);
    } else {
      const completedObjectives = Object.keys(sprintData[currentDay] || {}).length;
      const totalObjectives = getCurrentObjectives().length;
      setIsReadyForNextDay(completedObjectives === totalObjectives);
    }
  }, [currentDay, crazyEightIdeas, storyboardSections, sprintData, getCurrentObjectives]);

  useEffect(() => {
    checkDayCompletion();
  }, [crazyEightIdeas, storyboardSections, sprintData, checkDayCompletion]);

  useEffect(() => {
    let interval;
    if (isTimerRunning && timerSeconds > 0) {
      interval = setInterval(() => {
        setTimerSeconds(seconds => seconds - 1);
      }, 1000);
    } else if (timerSeconds === 0) {
      setIsTimerRunning(false);
    }
    return () => clearInterval(interval);
  }, [isTimerRunning, timerSeconds]);

  useEffect(() => {
    const initialPrompt = `Welcome to Day ${currentDay} of the Design Sprint. ${getCurrentPrompt()} As an expert design sprint coach, provide a brief, encouraging message to start the day. Remind the user of the objectives they need to complete.`;
    setIsTyping(true);
    getGPTResponse(initialPrompt).then(response => {
      const cleanedResponse = response.replace('[SHOW_NEXT_DAY_BUTTON]', '').trim();
      setFeedback(cleanedResponse);
      setIsTyping(false);
      setConversationLog([{ isUser: false, text: cleanedResponse }]);
    });
  }, [currentDay, getCurrentPrompt]);

  const startTimer = () => {
    setIsTimerRunning(true);
    setTimerSeconds(480);
  };

  const endTimer = () => {
    setIsTimerRunning(false);
    setIsCrazyEightComplete(true);
  };

  const addStickyNote = () => {
    if (newNote.trim()) {
      setStickyNotes(prev => [...prev, newNote]);
      setNewNote('');
    }
  };

  const deleteStickyNote = (index) => {
    setStickyNotes(prev => prev.filter((_, i) => i !== index));
  };

  const addCrazyEightIdea = () => {
    if (crazyEightIdeas.length < 8) {
      setCrazyEightIdeas(prev => [...prev, '']);
    }
  };

  const updateCrazyEightIdea = (index, value) => {
    setCrazyEightIdeas(prev => {
      const newIdeas = [...prev];
      newIdeas[index] = value;
      return newIdeas;
    });
  };

  const updateStoryboardSection = (index, value) => {
    setStoryboardSections(prev => {
      const newSections = [...prev];
      newSections[index] = value;
      return newSections;
    });
    checkDayCompletion();
  };

  const copyConversationLog = () => {
    const logText = conversationLog.map(entry => `**${entry.isUser ? 'User' : 'Relay'}**: ${entry.text}`).join('\n');
    navigator.clipboard.writeText(logText);
  };

  return (
    <SprintContainer>
      <MainContent>
        <Header>{`Day ${currentDay}: ${sprintGuidance[currentDay].goal}`}</Header>
        {isTyping ? (
          <TypingIndicator>
            <span></span>
            <span></span>
            <span></span>
          </TypingIndicator>
        ) : (
          feedback && (
            <AIMessage>
              <RelayIcon>Relay :)</RelayIcon> {feedback}
            </AIMessage>
          )
        )}
        <InputWithSendIcon
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onSubmit={handleSubmit}
          onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSubmit()}
          placeholder={currentDay === 2 ? "Need more instructions? Please ask!" : "Enter your response here..."}
        />
        {isReadyForNextDay && (
          <NextDayButton onClick={moveToNextDay}>
            Ready to move to Day {currentDay + 1}?
          </NextDayButton>
        )}
        {currentDay === 2 && (
          <>
            <CrazyEightContainer>
              <CrazyEightInstructions>
                Crazy 8s is a fast sketching exercise where you'll create 8 distinct ideas in 8 minutes. The goal is to push beyond your first idea and generate a wide range of possibilities. Don't worry about quality or feasibility at this stage - focus on quantity and variety!
              </CrazyEightInstructions>
              <CopyButton onClick={startTimer}>Start Crazy 8s Timer</CopyButton>
              <Timer>{Math.floor(timerSeconds / 60)}:{(timerSeconds % 60).toString().padStart(2, '0')}</Timer>
              {crazyEightIdeas.map((idea, index) => (
                <CrazyEightInput
                  key={index}
                  value={idea}
                  onChange={(e) => updateCrazyEightIdea(index, e.target.value)}
                  placeholder={`Crazy 8s Idea ${index + 1}`}
                />
              ))}
              {crazyEightIdeas.length < 8 && (
                <AddIdeaButton onClick={addCrazyEightIdea}>Add Idea</AddIdeaButton>
              )}
              {isTimerRunning && (
                <EndTimerButton onClick={endTimer}>End Timer and Move to Storyboard</EndTimerButton>
              )}
            </CrazyEightContainer>
            {isCrazyEightComplete && (
              <StoryboardContainer>
                <StoryboardInstructions>
                  Create a three-panel storyboard that outlines your chosen solution. Each panel should represent a key step or moment in your solution:
                  1. The problem or starting point
                  2. The main action or change
                  3. The result or outcome
                  Describe each panel in detail, focusing on what the user sees and does.
                </StoryboardInstructions>
                {storyboardSections.map((section, index) => (
                  <StoryboardSection
                    key={index}
                    value={section}
                    onChange={(e) => updateStoryboardSection(index, e.target.value)}
                    placeholder={`Describe panel ${index + 1}`}
                  />
                ))}
              </StoryboardContainer>
            )}
            {isReadyForNextDay && (
              <SubmitButton onClick={submitDay2}>
                Submit Day 2 and Move to Day 3
              </SubmitButton>
            )}
          </>
        )}
        <StickyNotesSection>
          <StickyNotePrompt>Add your quick thoughts to the sticky note board:</StickyNotePrompt>
          <StickyNotesGrid>
            {stickyNotes.map((note, index) => (
              <StickyNote key={index}>
                {note}
                <DeleteNote onClick={() => deleteStickyNote(index)}>×</DeleteNote>
              </StickyNote>
            ))}
          </StickyNotesGrid>
          <StickyNoteInput
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
            placeholder="Add a new note..."
            onKeyPress={(e) => e.key === 'Enter' && addStickyNote()}
          />
        </StickyNotesSection>
        <ConversationLog>
          <h3>Conversation Log (For Development)</h3>
          <CopyButton onClick={copyConversationLog}>Copy Log</CopyButton>
          {conversationLog.map((entry, index) => (
            <LogEntry key={index} isUser={entry.isUser}>
              <strong>{entry.isUser ? 'User' : 'Relay'}</strong>: {entry.text}
            </LogEntry>
          ))}
        </ConversationLog>
      </MainContent>
      <OutputSidebar
        sprintData={sprintData}
        currentDay={currentDay}
      />
    </SprintContainer>
  );
};

export default DaySprint;
