import React, { useState, useEffect, useCallback } from 'react';
import styled, { keyframes } from 'styled-components';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance } from '../utils/sprintResources';
import OutputSidebar from './OutputSidebar';
import InputWithSendIcon from './InputWithSendIcon';

// ... (keep existing styled components)

const AskQuestionButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 20px;
`;

const DaySprint = () => {
  // ... (keep existing state declarations)
  const [showQuestionInput, setShowQuestionInput] = useState(false);

  // ... (keep existing functions)

  const handleSubmit = async () => {
    if (!input.trim()) return;

    setIsTyping(true);
    const userInput = input;
    setInput('');

    setConversationLog(prev => [...prev, { isUser: true, text: userInput }]);

    const prompt = `Day ${currentDay} of Design Sprint. User input: "${userInput}". ${getCurrentPrompt()} Provide guidance and suggestions to help the user develop their own ideas. Do not provide direct answers or solutions. Encourage the user to think critically and come up with their own responses.`;

    try {
      const response = await getGPTResponse(prompt);
      const cleanedResponse = response.replace('[SHOW_NEXT_DAY_BUTTON]', '').trim();
      setFeedback(cleanedResponse);
      setConversationLog(prev => [...prev, { isUser: false, text: cleanedResponse }]);

      // Update sprint data
      const currentObjectives = getCurrentObjectives();
      const currentObjective = currentObjectives[Object.keys(sprintData[currentDay] || {}).length];

      if (currentObjective) {
        let summary;
        if (currentDay === 2) {
          summary = crazyEightIdeas.filter(idea => idea.trim() !== '');
        } else if (currentDay === 3) {
          summary = storyboardSections.map((section, index) => `Panel ${index + 1}: ${section}`);
        } else if (currentDay === 4) {
          summary = Object.values(prototypeData).join(' | ');
        } else if (currentDay === 5) {
          summary = `Testing Scenario: ${testingScenario}\nUser Interaction: ${userInteraction}\nAnalysis: ${analysisNotes}`;
        } else {
          summary = userInput;
        }

        setSprintData(prev => ({
          ...prev,
          [currentDay]: {
            ...prev[currentDay],
            [currentObjective]: summary
          }
        }));
      }

      checkDayCompletion();
    } catch (error) {
      console.error("Error getting AI response:", error);
      setFeedback("I'm having trouble providing feedback right now. Please try again.");
    } finally {
      setIsTyping(false);
    }
  };

  // ... (keep other existing functions)

  return (
    <SprintContainer>
      <MainContent>
        <Header>{`Day ${currentDay}: ${sprintGuidance[currentDay].goal}`}</Header>
        {isTyping ? (
          <TypingIndicator>
            <span></span>
            <span></span>
            <span></span>
          </TypingIndicator>
        ) : (
          feedback && (
            <AIMessage>
              <RelayIcon>Relay :)</RelayIcon> {feedback}
            </AIMessage>
          )
        )}
        {currentDay === 1 ? (
          <InputWithSendIcon
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onSubmit={handleSubmit}
            onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSubmit()}
            placeholder="Enter your response here..."
          />
        ) : (
          <>
            {!showQuestionInput && (
              <AskQuestionButton onClick={() => setShowQuestionInput(true)}>
                Ask a question
              </AskQuestionButton>
            )}
            {showQuestionInput && (
              <InputWithSendIcon
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onSubmit={handleSubmit}
                onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSubmit()}
                placeholder="Have any questions?"
              />
            )}
          </>
        )}
        {isReadyForNextDay && (
          <NextDayButton onClick={moveToNextDay}>
            Ready to move to Day {currentDay + 1}?
          </NextDayButton>
        )}
        {/* Day-specific components */}
        {currentDay === 2 && (
          <CrazyEightContainer>
            <CrazyEightInstructions>
              Crazy 8s is a fast sketching exercise where you'll create 8 distinct ideas in 8 minutes. 
              The goal is to push beyond your first idea and generate a wide range of possibilities. 
              Don't worry about quality or feasibility at this stage - focus on quantity and variety!
            </CrazyEightInstructions>
            <CopyButton onClick={startTimer}>Start Crazy 8s Timer</CopyButton>
            <Timer>{Math.floor(timerSeconds / 60)}:{(timerSeconds % 60).toString().padStart(2, '0')}</Timer>
            {crazyEightIdeas.map((idea, index) => (
              <CrazyEightInput
                key={index}
                value={idea}
                onChange={(e) => {
                  const newIdeas = [...crazyEightIdeas];
                  newIdeas[index] = e.target.value;
                  setCrazyEightIdeas(newIdeas);
                }}
                placeholder={`Crazy 8s Idea ${index + 1}`}
              />
            ))}
            {isReadyForNextDay && (
              <SubmitButton onClick={submitDay2}>
                Submit Crazy 8s and Move to Day 3
              </SubmitButton>
            )}
          </CrazyEightContainer>
        )}
        {currentDay === 3 && (
          <StoryboardContainer>
            <StoryboardInstructions>
              Create a three-panel storyboard that outlines your chosen solution. Each panel should represent a key step or moment in your solution:
              1. The problem or starting point
              2. The main action or change
              3. The result or outcome
              Describe each panel in detail, focusing on what the user sees and does.
            </StoryboardInstructions>
            {storyboardSections.map((section, index) => (
              <StoryboardSection
                key={index}
                value={section}
                onChange={(e) => updateStoryboardSection(index, e.target.value)}
                placeholder={`Describe panel ${index + 1}`}
              />
            ))}
            {isReadyForNextDay && (
              <SubmitButton onClick={submitDay3}>
                Submit Storyboard and Move to Day 4
              </SubmitButton>
            )}
          </StoryboardContainer>
        )}
        {currentDay === 4 && (
          <PrototypeSection>
            <h3>Design Your Conceptual Prototype</h3>
            <StoryboardSection
              value={prototypeData.coreElements}
              onChange={(e) => setPrototypeData(prev => ({ ...prev, coreElements: e.target.value }))}
              placeholder="Describe core elements..."
            />
            <StoryboardSection
              value={prototypeData.structureFlow}
              onChange={(e) => setPrototypeData(prev => ({ ...prev, structureFlow: e.target.value }))}
              placeholder="Outline structure or flow..."
            />
            <StoryboardSection
              value={prototypeData.interactionsProcesses}
              onChange={(e) => setPrototypeData(prev => ({ ...prev, interactionsProcesses: e.target.value }))}
              placeholder="Detail interactions or processes..."
            />
            {isReadyForNextDay && (
              <SubmitButton onClick={submitDay4}>
                Submit Conceptual Prototype and Move to Day 5
              </SubmitButton>
            )}
          </PrototypeSection>
        )}
        {currentDay === 5 && (
          <InteractionSection>
            <h3>Simulated User/Stakeholder Testing</h3>
            <StoryboardSection
              value={testingScenario}
              onChange={(e) => setTestingScenario(e.target.value)}
              placeholder="Describe a scenario for testing your prototype..."
            />
            <CopyButton onClick={conductSimulatedInteraction}>Conduct Simulated Interaction</CopyButton>
            {userInteraction && (
              <>
                <h4>Simulated User/Stakeholder Interaction:</h4>
                <p>{userInteraction}</p>
              </>
            )}
            <StoryboardSection
              value={analysisNotes}
              onChange={(e) => setAnalysisNotes(e.target.value)}
              placeholder="Analyze the feedback and consider potential improvements..."
            />
            {isReadyForNextDay && (
              <SubmitButton onClick={submitDay5}>
                Submit Testing Results and Complete Sprint
              </SubmitButton>
            )}
          </InteractionSection>
        )}
        {/* Commented out Sticky Notes section */}
        {/*
        <StickyNotesSection>
          ...
        </StickyNotesSection>
        */}
        <ConversationLog>
          <h3>Conversation Log (For Development)</h3>
          <CopyButton onClick={copyConversationLog}>Copy Log</CopyButton>
          {conversationLog.map((entry, index) => (
            <LogEntry key={index} isUser={entry.isUser}>
              <strong>{entry.isUser ? 'User' : 'Relay'}</strong>: {entry.text}
            </LogEntry>
          ))}
        </ConversationLog>
      </MainContent>
      <OutputSidebar
        sprintData={sprintData}
        currentDay={currentDay}
      />
    </SprintContainer>
  );
};

export default DaySprint;
