export const sprintGuidance = {
  1: {
    goal: "Set Long-Term Goal & Identify Critical Questions",
    objectives: [
      "Set the Long-Term Goal: Define a clear, specific, and measurable goal for the sprint.",
      "Identify Critical Questions: Convert potential challenges into questions to guide the sprint."
    ],
    prompt: "Let's start by defining your long-term goal for this project. What specific, measurable outcome are you aiming to achieve? Then, identify 2-3 critical questions that address potential challenges we need to overcome to reach this goal."
  },
  2: {
    goal: "Crazy 8s & Solution Sketch",
    objectives: [
      "Crazy 8s: Rapidly sketch eight different ideas in eight minutes, focusing on variety and creativity.",
      "Solution Sketch: Create a detailed, three-panel storyboard for the most promising idea from Crazy 8s."
    ],
    prompt: "Begin with the Crazy 8s exercise: quickly sketch eight diverse ideas in eight minutes. Then, select the most promising idea and create a detailed three-panel storyboard. Describe your process and final solution sketch."
  },
  3: {
    goal: "Heat Map Voting & Supervote",
    objectives: [
      "Heat Map Voting: Post solution sketches, and team members silently review and place dot stickers on the best parts.",
      "Supervote: The Decider makes the final decision on which solution to prototype."
    ],
    prompt: "Describe how you would conduct Heat Map Voting with your team. Then, explain the Supervote process and how the Decider would make the final decision on which solution to prototype."
  },
  4: {
    goal: "Build Realistic Prototype",
    objectives: [
      "Fake It: Build a realistic façade of the chosen solution, focusing on user-facing aspects.",
      "Stitch It Together: Combine all parts of the prototype into a cohesive whole, ensuring a seamless user experience."
    ],
    prompt: "Outline your plan for building a realistic prototype. How will you create a façade that focuses on user-facing aspects? Describe how you'll combine all parts into a cohesive, seamless user experience."
  },
  5: {
    goal: "Conduct User Interviews & Analyze Feedback",
    objectives: [
      "Interview 5 Target Customers: Conduct structured interviews to gather feedback, focusing on user interactions with the prototype.",
      "Analyze Feedback: Identify patterns and key insights from the interviews to determine if the prototype meets the long-term goal."
    ],
    prompt: "Describe your plan for interviewing 5 target customers. What key areas will you focus on during these structured interviews? Then, explain how you'll analyze the feedback to identify patterns and key insights that will help determine if your prototype meets the long-term goal."
  }
};

export const corePrinciples = [
  "Focus on High-Impact Activities: Prioritize tasks that provide the most value.",
  "Collaborate Efficiently: Leverage team expertise and maintain clear communication.",
  "Stay User-Centric: Keep the end-user experience at the forefront of all activities."
];

export const getSprintTip = (day) => {
  const tips = [
    "Keep your long-term goal in mind as you identify critical questions.",
    "During Crazy 8s, focus on quantity and variety of ideas rather than perfection.",
    "In Heat Map Voting, encourage silent, individual decision-making to avoid groupthink.",
    "When prototyping, focus on creating a realistic façade rather than building full functionality.",
    "During user interviews, focus on observing behavior and listening more than talking."
  ];
  return tips[day - 1] || "Remember to apply the core principles throughout your sprint!";
};
