import React, { useState, useCallback, useRef, useEffect } from 'react';
import styled from 'styled-components';
import SimpleMarkdown from 'simple-markdown';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance, getSprintTip } from '../utils/sprintResources';
import { createSprintDay, states } from './sprintStateMachine';

// Styled components remain the same
const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #1e1e1e;
  border-radius: 8px;
  padding: 1.5rem;
  font-family: 'Roboto', sans-serif;
  height: 60vh;
  overflow-y: auto;
`;

const Message = styled.div`
  background-color: ${props => props.isUser ? '#3c8ce7' : '#2a2a2a'};
  color: #ffffff;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
  align-self: ${props => props.isUser ? 'flex-end' : 'flex-start'};
  max-width: 80%;
`;

const RelayIcon = styled.span`
  color: #3c8ce7;
  margin-right: 0.5rem;
`;

const InputContainer = styled.div`
  display: flex;
  margin-top: 1rem;
  align-items: flex-end;
`;

const Input = styled.textarea`
  flex: 1;
  padding: 0.8rem;
  font-size: 1rem;
  border: none;
  border-radius: 4px;
  background-color: #2a2a2a;
  color: #ffffff;
  resize: none;
  min-height: 40px;
  overflow-y: hidden;
`;

const SendButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 0.8rem 1.5rem;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 4px;
  margin-left: 0.5rem;
  height: 40px;
`;

const NextDayButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 1rem;
`;

const MoreInfoButton = styled.button`
  background-color: transparent;
  color: #3c8ce7;
  border: 1px solid #3c8ce7;
  padding: 5px 10px;
  font-size: 0.8rem;
  cursor: pointer;
  border-radius: 4px;
  margin-top: 0.5rem;
`;

const parseMarkdown = (markdown) => {
  const rawBuiltParser = SimpleMarkdown.parserFor(SimpleMarkdown.defaultRules);
  const parser = (source) => {
    const blockSource = source + "\n\n";
    return rawBuiltParser(blockSource);
  };
  const reactOutput = SimpleMarkdown.reactFor(SimpleMarkdown.ruleOutput(SimpleMarkdown.defaultRules, 'react'));
  return reactOutput(parser(markdown));
};

const AIChat = ({ day, onUpdateDeliverable, onNextDay }) => {
  const sprintDay = createSprintDay(day);
  const [currentState, setCurrentState] = useState(sprintDay.initialState);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [dayData, setDayData] = useState({});
  const inputRef = useRef(null);

  useEffect(() => {
    const initialPrompt = `Welcome to Day ${day} of the mini design sprint! Our goal today is to ${sprintGuidance[day].goal}. Let's start by ${sprintDay.prompts[sprintDay.initialState]}`;
    setMessages([{ text: initialPrompt, isUser: false }]);
  }, [day, sprintDay.prompts, sprintDay.initialState]);

  const getAIResponse = useCallback(async (prompt, currentDay) => {
    setIsLoading(true);
    try {
      const aiResponse = await getGPTResponse(getAIPrompt(prompt, currentDay, messages, currentState, dayData));
      const aiMessage = { text: aiResponse, isUser: false };
      setMessages(prevMessages => [...prevMessages, aiMessage]);

      const nextState = sprintDay.transitions[currentState];
      if (nextState) {
        setCurrentState(nextState);
        if (nextState === states.COMPLETED) {
          const deliverable = summarizeDay(dayData);
          onUpdateDeliverable(currentDay, deliverable);
        }
      }
    } catch (error) {
      console.error('Error getting AI response:', error);
      setMessages(prevMessages => [...prevMessages, { 
        text: "I'm having trouble processing that. Let's try a different approach. " + getSprintTip(day), 
        isUser: false 
      }]);
    } finally {
      setIsLoading(false);
    }
  }, [currentState, sprintDay.transitions, onUpdateDeliverable, day, messages, dayData]);

  const handleSend = async () => {
    if (input.trim() && !isLoading) {
      const userMessage = { text: input, isUser: true };
      setMessages(prevMessages => [...prevMessages, userMessage]);
      setDayData(prevData => ({...prevData, [currentState]: input}));
      const currentInput = input;
      setInput('');
      await getAIResponse(currentInput, day);
    }
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
    e.target.style.height = 'auto';
    e.target.style.height = `${e.target.scrollHeight}px`;
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleNextDay = () => {
    onNextDay();
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = 'auto';
      inputRef.current.style.height = `${inputRef.current.scrollHeight}px`;
    }
  }, [input]);

  return (
    <>
      <ChatContainer>
        {messages.map((message, index) => (
          <Message key={index} isUser={message.isUser}>
            {!message.isUser && <RelayIcon>:-) Relay</RelayIcon>}
            {parseMarkdown(message.text)}
          </Message>
        ))}
      </ChatContainer>
      <InputContainer>
        <Input
          ref={inputRef}
          value={input}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          disabled={isLoading || currentState === states.COMPLETED}
          rows={1}
        />
        <SendButton onClick={handleSend} disabled={isLoading || currentState === states.COMPLETED}>
          {isLoading ? 'Thinking...' : 'Send'}
        </SendButton>
      </InputContainer>
      {currentState === states.COMPLETED && (
        <NextDayButton onClick={handleNextDay}>
          Move to Day {day + 1}
        </NextDayButton>
      )}
    </>
  );
};

const getAIPrompt = (userInput, day, context, currentState, dayData) => {
  const { goal } = sprintGuidance[day];
  const lastUserMessage = context[context.length - 1]?.text || '';

  return `Day ${day}, state: ${currentState}. Goal: ${goal}. 
  Current data: ${JSON.stringify(dayData)}. 
  User input: "${lastUserMessage}".
  If the response is sufficient, move to the next state. If not, explain why and ask for specific information.
  Be concise and direct. Respond in 1-2 sentences max.`;
};

const summarizeDay = (dayData) => {
  return Object.entries(dayData)
    .map(([key, value]) => `${key}: ${value}`)
    .join('\n');
};

export default AIChat;
