import React, { useState, useEffect, useCallback, useRef } from 'react';
import styled from 'styled-components';
import { debounce } from 'lodash';
import SimpleMarkdown from 'simple-markdown';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance, getSprintTip } from '../utils/sprintResources';

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #1e1e1e;
  border-radius: 8px;
  padding: 1.5rem;
  font-family: 'Roboto', sans-serif;
  height: 60vh;
  overflow-y: auto;
`;

const Message = styled.div`
  background-color: ${props => props.isUser ? '#3c8ce7' : '#2a2a2a'};
  color: #ffffff;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
  align-self: ${props => props.isUser ? 'flex-end' : 'flex-start'};
  max-width: 80%;
`;

const RelayIcon = styled.span`
  color: #3c8ce7;
  margin-right: 0.5rem;
`;

const InputContainer = styled.div`
  display: flex;
  margin-top: 1rem;
  align-items: flex-end;
`;

const Input = styled.textarea`
  flex: 1;
  padding: 0.8rem;
  font-size: 1rem;
  border: none;
  border-radius: 4px;
  background-color: #2a2a2a;
  color: #ffffff;
  resize: none;
  min-height: 40px;
  overflow-y: hidden;
`;

const SendButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 0.8rem 1.5rem;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 4px;
  margin-left: 0.5rem;
  height: 40px;
`;

const NextDayButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 1rem;
`;

const MoreInfoButton = styled.button`
  background-color: transparent;
  color: #3c8ce7;
  border: 1px solid #3c8ce7;
  padding: 5px 10px;
  font-size: 0.8rem;
  cursor: pointer;
  border-radius: 4px;
  margin-top: 0.5rem;
`;

const parseMarkdown = (markdown) => {
  const rawBuiltParser = SimpleMarkdown.parserFor(SimpleMarkdown.defaultRules);
  const parser = (source) => {
    const blockSource = source + "\n\n";
    return rawBuiltParser(blockSource);
  };
  const reactOutput = SimpleMarkdown.reactFor(SimpleMarkdown.ruleOutput(SimpleMarkdown.defaultRules, 'react'));
  return reactOutput(parser(markdown));
};

const AIChat = ({ day, tasks, onUpdateDeliverable, onNextDay }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [hasInitialized, setHasInitialized] = useState(false);
  const [showNextDayButton, setShowNextDayButton] = useState(false);
  const inputRef = useRef(null);

  const getAIResponse = useCallback(async (prompt, currentDay, shouldUpdateDeliverable = false) => {
    setIsLoading(true);
    try {
      const aiResponse = await getGPTResponse(getAIPrompt(prompt, currentDay, messages));
      const aiMessage = { text: aiResponse, isUser: false };
      setMessages(prev => [...prev, aiMessage]);
      if (shouldUpdateDeliverable) {
        const deliverable = await getGPTResponse(getDeliverablePrompt(prompt, currentDay, messages));
        onUpdateDeliverable(currentDay, deliverable);
      }

      if (aiResponse.includes("[SHOW_NEXT_DAY_BUTTON]")) {
        setShowNextDayButton(true);
      }
    } catch (error) {
      console.error('Error getting AI response:', error);
      setMessages(prev => [...prev, { 
        text: "I apologize, but I'm having trouble processing that request. Let's try a different approach. " + getSprintTip(day), 
        isUser: false 
      }]);
    } finally {
      setIsLoading(false);
    }
  }, [onUpdateDeliverable, day, messages]);

  useEffect(() => {
    if (!hasInitialized) {
      const initialPrompt = `Hi, I'm Relay, your AI assistant for this mini design sprint. Welcome to Day ${day}! ${sprintGuidance[day].prompt}`;
      setMessages([{ text: initialPrompt, isUser: false }]);
      setHasInitialized(true);
    }
  }, [day, hasInitialized]);

  useEffect(() => {
    if (day > 1) {
      getAIResponse(`Welcome to Day ${day}! ${sprintGuidance[day].prompt}`, day, false);
    }
  }, [day, getAIResponse]);

  const debouncedHandleSend = useCallback(
    debounce(async () => {
      if (input.trim() && !isLoading) {
        const userMessage = { text: input, isUser: true };
        setMessages(prev => [...prev, userMessage]);
        const currentInput = input;
        setInput('');
        await getAIResponse(currentInput, day, true);
      }
    }, 300),
    [input, isLoading, day, getAIResponse]
  );

  const handleInputChange = (e) => {
    setInput(e.target.value);
    e.target.style.height = 'auto';
    e.target.style.height = `${e.target.scrollHeight}px`;
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      debouncedHandleSend();
    }
  };

  const handleNextDay = () => {
    setShowNextDayButton(false);
    onNextDay();
  };

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = 'auto';
      inputRef.current.style.height = `${inputRef.current.scrollHeight}px`;
    }
  }, [input]);

  return (
    <>
      <ChatContainer>
        {messages.map((message, index) => (
          <Message key={index} isUser={message.isUser}>
            {!message.isUser && <RelayIcon>:-) Relay</RelayIcon>}
            {parseMarkdown(message.text)}
          </Message>
        ))}
      </ChatContainer>
      <InputContainer>
        <Input
          ref={inputRef}
          value={input}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          disabled={isLoading}
          rows={1}
        />
        <SendButton onClick={debouncedHandleSend} disabled={isLoading}>
          {isLoading ? 'Thinking...' : 'Send'}
        </SendButton>
      </InputContainer>
      {showNextDayButton && (
        <NextDayButton onClick={handleNextDay}>
          Move to Day {day + 1}
        </NextDayButton>
      )}
      {!isLoading && (
        <MoreInfoButton onClick={() => getAIResponse("Can you elaborate on that?", day, false)}>
          More Info
        </MoreInfoButton>
      )}
    </>
  );
};

const getAIPrompt = (userInput, day, context) => {
  const { goal, tasks } = sprintGuidance[day];
  const userMessages = context.filter(msg => msg.isUser).map(msg => msg.text);
  const lastUserMessage = userMessages[userMessages.length - 1] || '';

  return `You are Relay, an AI assistant for a mini design sprint. It's Day ${day}. Be extremely concise and direct.
  Goal: ${goal}
  Tasks: ${tasks.join(', ')}
  User's last message: "${lastUserMessage}"

  Respond in 1-2 short sentences max. Focus on the most important next step or question.
  If all tasks are addressed, include [SHOW_NEXT_DAY_BUTTON].
  Avoid pleasantries or repeating information.`;
};

const getDeliverablePrompt = (userInput, day, context) => {
  const { goal } = sprintGuidance[day];
  const allUserInputs = context.filter(msg => msg.isUser).map(msg => msg.text).join(' ');
  return `Summarize key points for Day ${day} based on: "${allUserInputs}"
  Goal: ${goal}
  Provide 2-3 bullet points max. Be extremely concise and specific.`;
};

export default AIChat;
