import React, { useState, useEffect, useCallback, useRef } from 'react';
import styled from 'styled-components';
import { debounce } from 'lodash';
import SimpleMarkdown from 'simple-markdown';
import { getGPTResponse } from '../utils/gptService';
import { sprintGuidance, getSprintTip } from '../utils/sprintResources';

const ChatContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 60vh;
  overflow-y: auto;
  background-color: #2a2a2a;
  border-radius: 8px;
  padding: 1rem;
`;

const Message = styled.div`
  background-color: ${props => props.isUser ? '#3c8ce7' : '#4a4a4a'};
  color: #ffffff;
  padding: 0.8rem;
  border-radius: 8px;
  margin-bottom: 1rem;
  align-self: ${props => props.isUser ? 'flex-end' : 'flex-start'};
  max-width: 70%;
  line-height: 1.5;

  p {
    margin-bottom: 0.5rem;
  }

  ul, ol {
    padding-left: 1.5rem;
    margin: 0.5rem 0;
  }

  li {
    margin-bottom: 0.25rem;
  }

  strong {
    font-weight: bold;
  }

  h1, h2, h3, h4, h5, h6 {
    margin-top: 1rem;
    margin-bottom: 0.5rem;
  }
`;

const InputContainer = styled.div`
  display: flex;
  margin-top: 1rem;
`;

const Input = styled.textarea`
  flex: 1;
  padding: 0.8rem;
  font-size: 1rem;
  border: none;
  border-radius: 4px;
  background-color: #3a3a3a;
  color: #ffffff;
  resize: none;
  min-height: 40px;
  max-height: 200px;
  overflow-y: auto;
`;

const SendButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 0.8rem 1.5rem;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 4px;
  margin-left: 0.5rem;
`;

const NextDayButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 1rem;
`;

const parseMarkdown = (markdown) => {
  const rawBuiltParser = SimpleMarkdown.parserFor(SimpleMarkdown.defaultRules);
  const parser = (source) => {
    const blockSource = source + "\n\n";
    return rawBuiltParser(blockSource);
  };
  const reactOutput = SimpleMarkdown.reactFor(SimpleMarkdown.ruleOutput(SimpleMarkdown.defaultRules, 'react'));
  return reactOutput(parser(markdown));
};

const AIChat = ({ day, onUpdateDeliverable, onNextDay }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [hasInitialized, setHasInitialized] = useState(false);
  const [showNextDayButton, setShowNextDayButton] = useState(false);
  const inputRef = useRef(null);

  const getAIResponse = useCallback(async (prompt, currentDay, shouldUpdateDeliverable = false) => {
    setIsLoading(true);
    try {
      const aiResponse = await getGPTResponse(getAIPrompt(prompt, currentDay, messages));
      const aiMessage = { text: aiResponse, isUser: false };
      setMessages(prev => [...prev, aiMessage]);
      if (shouldUpdateDeliverable) {
        const deliverable = await getGPTResponse(getDeliverablePrompt(prompt, currentDay, messages));
        onUpdateDeliverable(currentDay, deliverable);
      }

      // Check if it's time to show the next day button
      if (aiResponse.includes("[SHOW_NEXT_DAY_BUTTON]")) {
        setShowNextDayButton(true);
      }
    } catch (error) {
      console.error('Error getting AI response:', error);
      setMessages(prev => [...prev, { 
        text: "I apologize, but I'm having trouble processing that request. Let's try a different approach. " + getSprintTip(day), 
        isUser: false 
      }]);
    } finally {
      setIsLoading(false);
    }
  }, [onUpdateDeliverable, day, messages]);

  useEffect(() => {
    if (!hasInitialized) {
      const initialPrompt = "Hi, I'm Relay, your AI assistant for this mini design sprint. I'll guide you through each day of the sprint, helping you capture the essence of a full Design Sprint process. Let's get started with Day " + day + "! " + sprintGuidance[day].prompt;
      setMessages([{ text: initialPrompt, isUser: false }]);
      setHasInitialized(true);
    }
  }, [day, hasInitialized]);

  const debouncedHandleSend = useCallback(
    debounce(async () => {
      if (input.trim() && !isLoading) {
        const userMessage = { text: input, isUser: true };
        setMessages(prev => [...prev, userMessage]);
        const currentInput = input;
        setInput('');
        await getAIResponse(currentInput, day, true);
      }
    }, 300),
    [input, isLoading, day, getAIResponse]
  );

  const handleInputChange = (e) => {
    setInput(e.target.value);
    e.target.style.height = 'inherit';
    e.target.style.height = `${e.target.scrollHeight}px`;
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      debouncedHandleSend();
    }
  };

  const handleNextDay = () => {
    setShowNextDayButton(false);
    onNextDay();
  };

  return (
    <>
      <ChatContainer>
        {messages.map((message, index) => (
          <Message key={index} isUser={message.isUser}>
            {parseMarkdown(message.text)}
          </Message>
        ))}
      </ChatContainer>
      <InputContainer>
        <Input
          ref={inputRef}
          value={input}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          disabled={isLoading}
        />
        <SendButton onClick={debouncedHandleSend} disabled={isLoading}>
          {isLoading ? 'Thinking...' : 'Send'}
        </SendButton>
      </InputContainer>
      {showNextDayButton && (
        <NextDayButton onClick={handleNextDay}>
          Advance to Day {day + 1}
        </NextDayButton>
      )}
    </>
  );
};

const getAIPrompt = (userInput, day, context) => {
  const { goal, tasks } = sprintGuidance[day];
  return `You are Relay, an AI assistant helping with a mini design sprint. It's Day ${day} of the sprint. 
  The goal for today is: ${goal}. 
  Key tasks: ${tasks.join(', ')}. 
  The user input is: "${userInput}". 
  Previous context: ${JSON.stringify(context)}
  Provide guidance, ask relevant follow-up questions, and help the user progress through this day of the sprint. 
  Be concise, friendly, and focused on the day's objectives. If you feel the user has provided enough information for this day, include [SHOW_NEXT_DAY_BUTTON] in your response to allow the user to move to the next day. Avoid repeating questions or information.`;
};

const getDeliverablePrompt = (userInput, day) => {
  const { goal } = sprintGuidance[day];
  return `Based on this user input for Day ${day} of the mini design sprint: "${userInput}", 
  create a concise summary of the key decisions or outputs related to the day's goal: ${goal}. 
  Format the response as a brief, bullet-pointed list of no more than 3-5 items.`;
};

export default AIChat;
