import React from 'react';
import styled from 'styled-components';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  padding: 0 20px;
  text-align: center;
  background-color: #121212;
  color: #e0e0e0;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const Subtitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: #3c8ce7;
`;

const Description = styled.p`
  font-size: 1.2rem;
  max-width: 600px;
  margin-bottom: 2rem;
`;

const BetaBadge = styled.span`
  background-color: #3c8ce7;
  color: white;
  padding: 0.2rem 0.5rem;
  border-radius: 1rem;
  font-size: 0.8rem;
  vertical-align: super;
`;

const StartButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #2a6cb0;
  }
`;

const HomePage = ({ onStartSprint }) => {
  return (
    <HomeContainer>
      <Title>AI-Powered Mini Design Sprint <BetaBadge>Beta</BetaBadge></Title>
      <Subtitle>Innovate in 20 Minutes</Subtitle>
      <Description>
        Experience a condensed version of Jake Knapp and John Zeratsky's Design Sprint methodology. 
        In just 20 minutes, you'll define goals, sketch solutions, decide on the best approach, and plan user testing.
        Our AI guide will assist you through each step, providing insights and keeping you on track.
      </Description>
      <Description>
        Perfect for quick ideation, team alignment, or getting a taste of the full Design Sprint process.
        No prior experience necessary - just bring your creativity and an open mind!
      </Description>
      <StartButton onClick={onStartSprint}>Start Mini Sprint</StartButton>
    </HomeContainer>
  );
};

export default HomePage;
