export const sprintGuidance = {
  1: {
    goal: "Set Long-Term Goal & Identify Critical Questions",
    tasks: [
      "Define a clear, specific, and measurable goal",
      "Convert potential challenges into questions"
    ],
    prompt: "Let's start by defining your long-term goal for this project. What specific outcome are you aiming to achieve? Also, what critical questions do we need to address?"
  },
  2: {
    goal: "Crazy 8s & Solution Sketch",
    tasks: [
      "Rapidly sketch eight different ideas in eight minutes",
      "Create a detailed, three-panel storyboard for the most promising idea"
    ],
    prompt: "Based on yesterday's goal and questions, let's brainstorm solutions. Start with Crazy 8s by sketching eight different ideas in eight minutes. Then, create a detailed storyboard for the most promising idea."
  },
  3: {
    goal: "Heat Map Voting & Supervote",
    tasks: [
      "Post solution sketches and vote on the best parts",
      "Decider makes the final decision on the solution to prototype"
    ],
    prompt: "Let's review the solution sketches. Use Heat Map Voting to identify the best parts. The Decider will then make the final decision on which solution to prototype."
  },
  4: {
    goal: "Build Realistic Prototype",
    tasks: [
      "Build a realistic façade of the chosen solution",
      "Combine all parts of the prototype into a cohesive whole"
    ],
    prompt: "Let's build a realistic prototype of the chosen solution. Focus on the user-facing aspects and ensure it provides a seamless user experience."
  },
  5: {
    goal: "Conduct User Interviews & Analyze Feedback",
    tasks: [
      "Interview 5 target customers",
      "Identify patterns and key insights from the interviews"
    ],
    prompt: "To validate your prototype, conduct structured interviews with 5 target customers. Focus on observing their interactions and gather feedback to determine if the prototype meets the long-term goal."
  }
};

export const getSprintTip = (day) => {
  const tips = [
    "Remember to keep your long-term goal in mind and convert challenges into guiding questions.",
    "Don't be afraid to think outside the box during Crazy 8s and focus on creating a detailed storyboard.",
    "Use Heat Map Voting to identify the best parts of the sketches and let the Decider finalize the solution.",
    "Focus on building a realistic and user-facing prototype that provides a seamless experience.",
    "Observe user interactions carefully during interviews and gather valuable feedback to refine your prototype."
  ];
  return tips[day - 1] || "Keep pushing forward!";
};
