import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import AIChat from './AIChat';
import OutputSidebar from './OutputSidebar';
import { sprintGuidance } from '../utils/sprintResources';

const SprintContainer = styled.div`
  display: flex;
  height: 100vh;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 2rem;
  overflow-y: auto;
`;

const DayTitle = styled.h2`
  font-size: 2rem;
  margin-bottom: 1rem;
`;

const DaySummary = styled.div`
  background-color: #3a3a3a;
  padding: 1rem;
  border-radius: 8px;
  margin-bottom: 1rem;
`;

const NextButton = styled.button`
  background-color: #3c8ce7;
  color: #ffffff;
  border: none;
  padding: 10px 20px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin-top: 1rem;
`;

const DaySprint = () => {
  const [currentDay, setCurrentDay] = useState(1);
  const [deliverables, setDeliverables] = useState({});

  const handleUpdateDeliverable = useCallback((day, deliverable) => {
    setDeliverables(prev => ({ ...prev, [day]: deliverable }));
  }, []);

  const handleNextDay = () => {
    if (currentDay < 5) {
      setCurrentDay(prev => prev + 1);
    }
  };

  const { goal, tasks } = sprintGuidance[currentDay];

  return (
    <SprintContainer>
      <MainContent>
        <DayTitle>{`Day ${currentDay}: ${goal}`}</DayTitle>
        <DaySummary>
          <h3>Today's Tasks:</h3>
          <ul>
            {tasks.map((task, index) => (
              <li key={index}>{task}</li>
            ))}
          </ul>
        </DaySummary>
        <AIChat 
          day={currentDay} 
          onUpdateDeliverable={handleUpdateDeliverable} 
        />
        {currentDay < 5 && (
          <NextButton onClick={handleNextDay}>Next Day</NextButton>
        )}
      </MainContent>
      <OutputSidebar 
        deliverables={deliverables} 
        currentDay={currentDay} 
      />
    </SprintContainer>
  );
};

export default DaySprint;
